//_________________________GENERIC FUNCTIONS_____________________________________
#define SUN_COLOR vec3(0.894,0.702,1.000)

// Struct to hold material values
struct materialVal {
    vec3 color;
    vec3 normal;
    float mask;
};

// Phong lighting model
vec3 phongLighting(vec3 albedo, vec3 normal, vec3 cameraDir, vec3 lightDir) {
    // Ambient
    float ambientStrength = 0.35;
    vec3 ambient = ambientStrength * albedo;

    // Diffuse 
    float diff = clamp(dot(normal, lightDir), 0.0, 1.0);
    vec3 diffuse = diff * albedo;

    // Specular
    float specularStrength = 1.5;
    vec3 viewDir = normalize(cameraDir);
    vec3 reflectDir = reflect(-lightDir, normal);  
    float spec = pow(clamp(dot(viewDir, reflectDir), 0.0, 1.0), 16.0);
    vec3 specular = specularStrength * spec * SUN_COLOR;  

    // Combine results
    return ambient + diffuse + specular;
}

// This function creates a simple sun with a halo effect.
#define ONE_OVER_PI 0.3183098861836
vec3 sun(vec3 normal, vec3 lightDir) {
    // Compute the dot product of the normal and light direction.
    float NdotL = dot(normal, lightDir);

    // Prevent Nan errors with fron acos
    NdotL = clamp(NdotL, -1., 1.);

    // Compute the angle between the normal and light direction.
    float angle = acos(NdotL) * ONE_OVER_PI;

    // Compute an intensity based on the angle.
    float intensity = pow(1.0 - angle, 50.0) * 3.0;

    // Generate a color based on the intensity and the sun's color.
    return intensity * SUN_COLOR;
}

// Linearly increases contrast of Alpha
// See: https://www.desmos.com/calculator/hs1nsjb32q
float straightContrast(float alpha, float contrast) {
    return clamp(contrast * (alpha - 0.5) + 0.5, 0., 1.);
}

// makes viridis colormap with polynimal 6
vec3 viridis(float t) {
    const vec3 c0 = vec3(0.274344,0.004462,0.331359);
    const vec3 c1 = vec3(0.108915,1.397291,1.388110);
    const vec3 c2 = vec3(-0.319631,0.243490,0.156419);
    const vec3 c3 = vec3(-4.629188,-5.882803,-19.646115);
    const vec3 c4 = vec3(6.181719,14.388598,57.442181);
    const vec3 c5 = vec3(4.876952,-13.955112,-66.125783);
    const vec3 c6 = vec3(-5.513165,4.709245,26.582180);
    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
}

// Safe Normalization avoiding division by zero with alternate
#define SMALLESTFLOAT (1.0 / float(0xffffffffU))
vec3 safeNormalize(vec3 vector, vec3 alternate) {

    // Calculate the square of the length of the vector
    float lenSq = dot(vector, vector);

    // Check if the squared length is greater than a tiny threshold
    if (lenSq > SMALLESTFLOAT) {
        // If so, normalize the vector resuing the squared length
        return vector / sqrt(lenSq);

    } else {
        // Otherwise, return a alternate vector
        return alternate;
    }
}

// Function for generating normals using 3 texture samples.
// Original source: https://www.shadertoy.com/view/3sSSW1
vec3 HeightToNormal(sampler2D tex, vec2 uv, vec2 res, float strength) {
    vec2 s = 1.0 / res;

    float textureOffset = 1.0;

    float p = texture(tex, uv).x;
    float h1 = texture(tex, uv + s * vec2(textureOffset,0)).x;
    float v1 = texture(tex, uv + s * vec2(0,textureOffset)).x;

   	vec2 xy = (p - vec2(h1, v1));

    return safeNormalize(vec3(xy * strength, 1.0), vec3(0,0,1));
}

