/*
Based on domrally's Squoval project.
    https://domrally.github.io/squoval
Asymptotically smooth & periodically flat functions.
    https://en.wikipedia.org/wiki/Smoothness
    https://en.wikipedia.org/wiki/Flat_function
*/
void mainImage(out vec4 o, vec2 i) {
    vec2 R = iResolution.xy;
    float r = min(R.x, R.y);
    vec2 uv = i / r;
    vec2 x = 2.1 * abs(fract(4. * uv) - .5);

    /*
    Inverse error function method by Sergei Winitzki using PadÃ© approximations
        https://en.wikipedia.org/wiki/Error_function#Approximation_with_elementary_functions    
    */
    vec2 c = log(1. - x * x),
         b = 4.33 + c / 2.,
         a = sqrt(b * b - c / .147),
         z = tanh(sign(x) * sqrt(a - b));
    z *= z;
    float d = 1. - z.x - z.y;

    vec3 color = pow(.6 + .25 * cos(iTime + 9. * floor(4. * uv.xyx) / 4. + vec3(0, 2, 4)), vec3(2.2));
    o.rgb = color * smoothstep(0., 9. * 4. * 2.1 / r, d);
    o.a = 1.;
}

const float PI = 3.1415927;

void mainImage(out vec4 O, vec2 I) {
    vec2 R = iResolution.xy;
    vec2 p = (2.0 * I - R) / R.y * 5.0;
    float t = 1.0 - abs(1.0 - mod(iTime / 2.0, 2.0));
    float a = (smoothstep(0.0, 1.0, smoothstep(0.0, 1.0, t)) - 0.5) * PI;
    float b = iMouse.z > 0.0 ? mix(0.2, 1.5, iMouse.x / R.x) : 0.6;
    vec4 T = vec4(cos(a), -cos(a), sin(a), -sin(a));
    vec3 o = vec3(0.0, 1.0, -1.0);
    mat2 A = mat2((T.xyxx + T.zzwz) * b + o.yzyy);
    vec4 Q = 2.0 * round(((inverse(A) * p).xyxy + o.xxyy) / 2.0) - o.xxyy;
    vec4 d = abs(vec4(A * mat2(Q)) - p.xyxy);
    vec2 e = max(d.xz, d.yw);
    vec4 f = abs(vec4(mat2(T.xwzx) * (mat2(p, p) - A * mat2(Q.xwzy))));
    vec2 g = max(f.xz, f.yw);
    vec2 uv = min(e.x, e.y) <= 1.0
        ? p - (A + mat2(b * o.yyzy + o.zyzz)) * (e.x < e.y ? Q.xy : Q.zw)
        : mat2(T.wyxw) * p +
          mat2(T.xxyx + T.zwzz + o.yzyy) * (g.x < g.y ? Q.xw : Q.zy);
    uv = (uv / 5.0 * R.y + R) / 2.0 / R;
    O = min(e.x, e.y) <= 1.0 || min(g.x, g.y) <= b
        ? texture(iChannel0, uv)
        : vec4(0.0);
}

const float ITERATIONS = 250.0;


// Convert a vec3 from hsv to rgb colour space
vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


float mandelbrot(float r, float i)
{
    float zr = 0.0;
    float zi = 0.0;
    float n = 0.0;
    while(zr * zr + zi * zi <= 4.0 && n < ITERATIONS)
    {

        float temp = zr * zr - zi * zi;
        zi = zr * zi + zi * zr + i;
        zr = temp + r;

        n += 1.0;
    }

    return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;  // Centered and aspect-corrected


    z /= iTime;
    z += vec2(-1.0, -.3);

    float m = mandelbrot(z.x, z.y);
    float c = 1.0 - m / ITERATIONS;

    vec3 hsv = vec3(pow(c, 2.2), 1.0, c);

    vec3 col = hsv2rgb(hsv);

    fragColor = vec4(col,1.0);
}

// CC0: Simple Anti aliasing of distance field border demonstration
//  For my friends in the Captain Coder discord
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // q is normalize to range [0,1]
    vec2 q = fragCoord/iResolution.xy;

    // p is in range [-1,1]
    vec2 p = -1.0+2.0*q;
    // Adjust for screen ratio, so that the circle actually looks like circle.
    p.x *= iResolution.x/iResolution.y;

    // From FabriceNeyrat2 in the comments
    //  A shorter way to compute p adjusted for screen resolution and ratio
    // vec2 p = ( 2.*fragCoord - iResolution.xy ) / iResolution.y;

    // Uses the size of the pixel to determine an aa factor
    float aa = sqrt(2.0)/iResolution.y;

    vec3 col = vec3(0.0);

    // Circle distance field
    float d = length(p) - 0.5;
    // Create a thin outline
    float od = abs(d-0.05)-aa;

    // Smoothstep the distance field combined with the aa factor to create
    //  a smooth border with little aliasing.
    col = mix(col, vec3(1.0), smoothstep(aa, -aa, d));
    // And the outline
    col = mix(col, vec3(1.0), smoothstep(aa, -aa, od));

    // Approximative linear RGB to sRGB conversion.
    col = sqrt(col);

    fragColor = vec4(col,1.0);
}

float band(int hz) { 
    int ix = int(float(hz)/23.); 
    return texelFetch( iChannel0, ivec2(ix,0), 0 ).x; 
}

float it() {
    return iTime * 1.2;
}

vec2 init(vec2 fragCoord, vec2 res) {
    float ratio = res.y/res.x;
    vec2 mr = vec2(min(res.x, res.y));
    vec2 uv = (fragCoord/mr) + vec2(-0.5/ratio, -0.5);
