    uv.x += sin(it());
    uv.y += cos(it());
    GA2 p = ga(.5, uv.x, uv.y, .8);
    GA2 q = ga(
        uv.x*band(400)*8., 
        sin(it()) * band(400)*18.,
        cos(it()) * band(400)*18., 
        uv.y*band(400)*8.);

    GA2 s = invert(mul(mul(band(100),p),q));

    uv = toVec2(s);

    int N = 100;

    float ra = band(200)*.01;
    float rb = band(200)*(.35-band(4000))*.09;//sin(iTime * 1.037) * 0.08;

    int n = mandel(uv.yx, vec2(.57+ra, .57+rb), N);

    vec3 col = n == N ? vec3(0) : vec3(
        sinp(nn(n) / 43.), 
        sinp(nn(n) / 27.)/2., 
        sinp(nn(n) * .2)
    );

    fragColor = vec4(col,1.0);
}

