/*
This is a small demonstration of how to implement uniplanar mapping with accurate 
normals. This is a variation of standard triplanar mapping that uses dithering to
only require one sample. This effect looks virtually identical to triplanar mapping,
especially when temporal anti-aliasing is also used, while being ~3x faster.

Several global constants are defined below to enable debug views of the
mapping, as well as mouse controls for navigation.

This method was introduced by Far Cry 5:
https://www.gdcvault.com/play/1025480/Terrain-Rendering-in-Far-Cry

See here how the same shader looks with triplanar mapping: 
https://www.shadertoy.com/view/mdcBz8
*/

// The number of previous samples looked at to blur result, with a similar effect to
// Temporal Anti-Aliasing (TAA) which you should use instead if you can.
#define TEMPORALBLUR 8 

// A value of 1 means no contrast, higher values increase contrast
#define CONTRAST 16.0

// This visualizes the blend region between the two cubemappings
// Green, Red and Blue represent the lower bounds of the weights
//#define SHOWGRID
#define LINETHICKNESS 0.01

// Valve edition (slightly modified to be a true 12 dither)
// http://alex.vlachos.com/graphics/Alex_Vlachos_Advanced_VR_Rendering_GDC2015.pdf
// Input in pixels (i.e., not normalized UV)
float ScreenSpaceDither12(vec2 vScreenPos, float time)
{
    float vDither = dot( vec2( 171.0, 231.0 ), vScreenPos.xy + time); 
    return fract( vDither / 103.0);
}

// Structure to hold material properties
struct materialValues {
    vec3 color;
    vec3 normal;
};

// Uniplanar mapping
materialValues uniplanar(sampler2D tex, vec3 normal, vec2 fragCoord, 
                         float time, float scale, float contrast) {
    // Weights/Alpha for interpolation
    vec3 weights = abs(normal);

    // Increase contrast of weights for sharper transitions
    weights = smoothContrast(weights, contrast);

    // Calculate sign for flipping sides
    vec3 sgn = sign(normal);

    // Scale coordinates. Replace normal with world coordinates if not a sphere
    vec3 uvwC = normal * scale;

    // Sample dither noise
    float dither = ScreenSpaceDither12(fragCoord, time);

    // Smooth contrast creates artifacts in the dither. Clamping removes them
    dither = clamp(dither, 0.01, 0.99);

    // Mip calculation as the automatic ones don't work
    vec3 duvwdx = dFdx(uvwC);
    vec3 duvwdy = dFdy(uvwC);

    // "Interpolate" the UVs using dither
    vec2 uv;
    vec2 duvdx; 
    vec2 duvdy;
    mat3 tbn;
    // The weights are grouped cumulatively, and if the dither value is within one 
    // cumulative section for one of the weights, that weight is selected.
    if (weights.x > dither) {
        // X-axis Side
        // Coordinate projections all right side up with slightly different offsets
        uv = vec2(uvwC.y * sgn.x, uvwC.z) + 0.25;

        // Partial derivative measures UV rate of change, so mirroring and offset are irrelevant.
        duvdx = duvwdx.yz; 
        duvdy = duvwdy.yz;

        // Tangent is the direction of uvX.x in world space
        vec3 tangentX = safeNormalize(cross(normal, vec3(0,0,-1)), vec3(0,sgn.x,0));   

        // Bitangent is the direction of uvY.y in world space.
        vec3 bitangentX = cross(normal, vec3(0,sgn.x,0));

        // Tangent Bitangent Normal matrix to translate normal textures to world space normals.
        tbn = mat3(tangentX, 
                   bitangentX, 
                   normal); // Normal vector straight up from the surface.

    } else if (1.-weights.z > dither) { // Because sum of all weights is 1, so 1-z = sum of xy.
        // Y-axis Side
        uv = vec2(-uvwC.x * sgn.y, uvwC.z) + 0.5;
        duvdx = duvwdx.zx;
        duvdy = duvwdy.zx;

        // TBN matric for the Y sides
        vec3 tangentY = safeNormalize(cross(normal, vec3(0,0,-1)), vec3(-sgn.y,0,0));
        vec3 bitangentY = cross(normal, vec3(-sgn.y,0,0));
        tbn = mat3(tangentY,
                   bitangentY,
                   normal);
    } else {
        // Z-axis Side 
        uv = vec2(uvwC.x * sgn.z, uvwC.y) + 0.75;        
        duvdx = duvwdx.xy;
        duvdy = duvwdy.xy;

        // TBN matric for the Z sides
        vec3 tangentZ = safeNormalize(cross(normal, vec3(0,-1,0)), vec3(sgn.z,0,0));
        vec3 bitangentZ = cross(normal, vec3(sgn.z,0,0));
        tbn = mat3(tangentZ,
                   bitangentZ,
                   normal);
    }

    // Sample textures for cardinal directions.
	vec3 color = textureGrad(tex, uv, duvdx, duvdy).xyz;

    // Sample normal. If possible use a normal texture instead in tangent space.
    vec3 tsNormal = heightToNormal(tex, uv, duvdx, duvdy, 
                                  iChannelResolution[0].xy, 5.0);

    // Translate normals to world space.
    vec3 normalWS = tbn * tsNormal;          

    #ifdef SHOWGRID
        // Overlaying the edges of the alpha mask.
        vec3 mask = step(abs(weights - 0.1), vec3(LINETHICKNESS));
        color = max(mask, color);
    #endif
    return materialValues(color, normalWS);
}

// used as part for variantance restoration see triplanar version for proper use
// NB: trick published in https://hal.inria.fr/inria-00536064v2
float getSquaredAlphaSum(vec3 normal, float contrast) {
    // Weights/Alpha for interpolation
    vec3 weights = abs(normal);

    // Increase contrast of weights for sharper transitions
    weights = smoothContrast(weights, contrast);

    // partial value used in main
    float squaredAlphaSum = weights.x * weights.x +
                            weights.y * weights.y +
                            weights.z * weights.z ; // = dot(weights, weights)

    return squaredAlphaSum; 
}

// _____________________________3D Section_____________________________
// https://www.shadertoy.com/view/4sjXW1 by nimitz (twitter: @stormoid)

mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}

vec2 iSphere(in vec3 ro, in vec3 rd) {
    vec3 oc = ro;
    float b = dot(oc, rd);
    float c = dot(oc,oc) - 1.;
    float h = b*b - c;
    //if(h <0.0) return vec2(-1.);
    return vec2(-b - sqrt(h), h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {	
	vec2 p = fragCoord.xy/iResolution.xy-0.5;
	p.x*=iResolution.x/iResolution.y;
	vec2 um = iMouse.xy / iResolution.xy-.5;
	um.x *= iResolution.x/iResolution.y;
    p*= 1.5;

    //camera
	vec3 ro = vec3(0.,0.,2.4);
    vec3 rd = normalize(vec3(p,-1.5));
    mat2 mx;
    mat2 my;
    vec2 cursor;
    if(iMouse.x > 0.0)	{    
        mx = mm2(um.x*5.);
        my = mm2(um.y*5.);   
    } else {
        mx = mm2(-iTime*.1+um.x*5.);
        my = mm2(iTime*0.05+um.y*5.);
    }
    ro.xz *= mx;rd.xz *= mx;
    ro.xy *= my;rd.xy *= my;

    vec2 t = iSphere(ro,rd);

    vec3 lightDir = normalize(vec3(1,0,1));

    // Alternate between background and sphere mapping
    vec3 color;
    if (t.x > 0.) {
        // Sphere mapping
    	vec3 normal = ro + rd * t.x;

        #if TEMPORALBLUR <= 1
            // We just take a single sample from the ditheredSample function.
            materialValues planar = uniplanar(iChannel0, normal, 
                    fragCoord, iTime, 0.7, CONTRAST);
        #else
            // We take multiple samples from the ditheredSample function and average them.
            // This is normally done automatically by your TAA or motion blur
            materialValues planar;
            for(int i = 0; i < TEMPORALBLUR; i++) {
                // subtracting from time offset
                materialValues tempPlanar = uniplanar(iChannel0, normal, 
                    fragCoord, iTime - float(i), 0.7, CONTRAST);

                // collect values 
                planar.color += tempPlanar.color;
                planar.normal += tempPlanar.normal;
            }
            planar.color /= float(TEMPORALBLUR);

            // Variance restoration with normalization (very visible in the Stars)
            float squaredAlphaSum = getSquaredAlphaSum(normal, CONTRAST);

            // Sampling mips lowest level to get the mean. Could be a constant if known.
            vec3 mean = vec3(0.576,0.455,0.404); // textureLod(tex, uvX, 12.).xyz; 

            // Variance restoration with normalization (very visible in the Stars)
            planar.color = mean + (planar.color - mean) / sqrt(squaredAlphaSum);

            // technically wrong but looks the same as Slerp
            planar.normal = normalize(planar.normal / float(TEMPORALBLUR)); 
        #endif

        #ifdef SHOWGRID
            // Show unlit color
            color = planar.color;
        #else
            // Apply lighting to show the normals too
            color = phongLighting(planar.color, planar.normal, ro, lightDir);
        #endif       
    } else {
        // Background mapping
        vec3 normal = rd;       

        // Sample albedo textures for stars
        // disabling dithering as the edge is invisible anyway with stars
        materialValues planar = uniplanar(iChannel1, normal, vec2(1), 0., 2., CONTRAST);

        #ifdef SHOWGRID
            // Show unlit color
            color = planar.color;
        #else        
            // Make stars less bright
            float stars = pow(planar.color.x * planar.color.y * planar.color.z,2.);
            vec3 albedo = planar.color * stars; // Make stars colorful

            // Create sun with halo
            vec3 sun = sun(normal, lightDir);

            // Combine stars albedo and sunlight
            color = max(albedo - sun, 0.) + sun;
        #endif 
    }

    #ifndef SHOWGRID
        // Fresnel mask for atmosphere
        float fresnel = pow(clamp(1.-abs(t.y), 0., 1.), 32.);
        color = mix(color, vec3(0.404,0.302,0.275), fresnel);
    #endif

	fragColor = vec4(color, 1.0);
}

