if (time < 5.0) {
transition = smoothstep(0.0, 5.0, time);
} else {
transition = smoothstep(5.0, 0.0, time - 5.0); // invertido
}

float d;
if (time < 5.0) {
d = mix(sdCircle(q, 0.5), sdSquare(q, 0.5), transition);
} else {
d = mix(sdSquare(q, 0.5), sdCircle(q, 0.5), transition);
}

vec3 col = vec3(0.0);
