vec2 closest(vec2 points[numOfPoints], vec2 pos) {
    int current = 0;
    float l = d(pos, points[0]);
    for(int i = 1; i < numOfPoints; i++) {
        float nLen = d(pos, points[i]);
        if( l < nLen ) {
            l = nLen;
            current = i;
        }
    }
    return points[current];
}

