/*
This is a small demonstration of how to implement triplanar mapping with accurate 
normals. I noticed that other implementations lacked clear explanations for their 
normal to world space mathematics or comments. Therefore, I derived it from scratch 
by constructing the tangent space matrices for the three projections. This mapping 
also ensures that all textures are oriented correctly.

Several global constants are defined below to enable debug views of the
mapping, as well as mouse controls for navigation.

See here for a detailed view of the TBN matrix for triplanar:
https://www.shadertoy.com/view/md3BzS

See here how to make this ~3x faster while looking the same: 
https://www.shadertoy.com/view/Ds3BzS
*/

// A value of 1 means no contrast, higher values increase contrast
#define CONTRAST 16.0

// This visualizes the blend region between the two cubemappings
// Green, Red and Blue represent the upper and lower bounds of the weights
//#define SHOWGRID
#define LINETHICKNESS 0.01

// Structure to hold material properties
struct materialValues {
    vec3 color;
    vec3 normal;
};

// Triplanar mapping
materialValues triplanar(sampler2D tex, vec3 normal, float scale, float contrast) {   
    // Calculate sign for flipping sides
    vec3 sgn = sign(normal);

    // Scale coordinates. Replace normal with world coordinates if not a sphere
    vec3 uvwC = normal * scale;

    // Coordinate projections all right side up with slightly different offsets
    vec2 uvX = vec2(uvwC.y * sgn.x, uvwC.z) + 0.25; 
    vec2 uvY = vec2(-uvwC.x * sgn.y, uvwC.z) + 0.5; 
    vec2 uvZ = vec2(uvwC.x * sgn.z, uvwC.y) + 0.75; 

    // Sample textures for cardinal directions
	vec3 colorX = texture(tex, uvX).xyz;
	vec3 colorY = texture(tex, uvY).xyz;
	vec3 colorZ = texture(tex, uvZ).xyz;

    // Sample normal, if possible use a normal texture instead in tangent space
    vec3 tsNormalX = heightToNormal(tex, uvX, iChannelResolution[0].xy, 5.0);
    vec3 tsNormalY = heightToNormal(tex, uvY, iChannelResolution[0].xy, 5.0);
    vec3 tsNormalZ = heightToNormal(tex, uvZ, iChannelResolution[0].xy, 5.0);

    // Tangent is the direction of uvX.x in world space
    vec3 tangentX = safeNormalize(cross(normal, vec3(0,0,-1)), vec3(0,sgn.x,0));  

    // Bitangent is the direction of uvY.y in world space. Is not perpendicular to tangent!
    vec3 bitangentX = cross(normal, vec3(0,sgn.x,0));

    // Tangent Bitangent Normal (tbn) matrix to translate normal textures to world space normals
    mat3 tbnX = mat3(tangentX, 
                     bitangentX, 
                     normal); // Normal vector straight up from the surface

    // TBN matric for the Y sides
    vec3 tangentY = safeNormalize(cross(normal, vec3(0,0,-1)), vec3(-sgn.y,0,0));
    vec3 bitangentY = cross(normal, vec3(-sgn.y,0,0));
    mat3 tbnY = mat3(tangentY,
                     bitangentY,
                     normal);

    // TBN matric for the Z sides
    vec3 tangentZ = safeNormalize(cross(normal, vec3(0,-1,0)), vec3(sgn.z,0,0));
    vec3 bitangentZ = cross(normal, vec3(sgn.z,0,0));
    mat3 tbnZ = mat3(tangentZ,
                     bitangentZ,
                     normal);

    // Translate normals to world space
    vec3 wsNormalX = tbnX * tsNormalX;
    vec3 wsNormalY = tbnY * tsNormalY;
    vec3 wsNormalZ = tbnZ * tsNormalZ;

    // Weights/Alpha for interpolation
    vec3 weights = abs(normal);

    // Increase contrast of weights for sharper transitions
    weights = smoothContrast(weights, contrast);

    // Color interpolation
    vec3 color = colorX * weights.x + 
                 colorY * weights.y + 
                 colorZ * weights.z;

    // Interpolate normals. Slerp may be better but the difference is not noticeable.
    vec3 normalWS = normalize(wsNormalX * weights.x + 
                              wsNormalY * weights.y + 
                              wsNormalZ * weights.z);

    // NB: trick published in https://hal.inria.fr/inria-00536064v2
    // Deeper conservation: see histogram-preserving here: https://hal.inria.fr/hal-01824773
    // Modified version of FabriceNeyret2 https://www.shadertoy.com/view/4dcSDr 
    float squaredAlphaSum = weights.x * weights.x +
                            weights.y * weights.y +
                            weights.z * weights.z ; // = dot(weights, weights)         

    // Sampling mips lowest level to get the mean. Can be a constant if known.
    vec3 mean = textureLod(tex, uvX, 12.).xyz;//  vec3(0.576,0.455,0.404);

    // Variance restoration with normalization (very visible in the Stars)
    color = mean + (color - mean) / sqrt(squaredAlphaSum);

    #ifdef SHOWGRID
        // Overlaying the edges of the alpha mask
        vec3 mask = step(abs(weights - 0.1), vec3(LINETHICKNESS));
        color = max(mask, color);
    #endif
    return materialValues(color, normalWS);
}

// _____________________________3D Section_____________________________
// https://www.shadertoy.com/view/4sjXW1 by nimitz (twitter: @stormoid)

mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}


vec2 iSphere(in vec3 ro, in vec3 rd) {
    vec3 oc = ro;
    float b = dot(oc, rd);
    float c = dot(oc,oc) - 1.;
    float h = b*b - c;
    //if(h <0.0) return vec2(-1.);
    return vec2(-b - sqrt(h), h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 p = fragCoord.xy/iResolution.xy-0.5;
	p.x*=iResolution.x/iResolution.y;
	vec2 um = iMouse.xy / iResolution.xy-.5;
	um.x *= iResolution.x/iResolution.y;
    p*= 1.5;

    //camera
	vec3 ro = vec3(0.,0.,2.4);
    vec3 rd = normalize(vec3(p,-1.5));
    mat2 mx;
    mat2 my;
    if(iMouse.x > 0.0)	{    
        mx = mm2(um.x*5.);
        my = mm2(um.y*5.);
    } else {
        mx = mm2(-iTime*.1+um.x*5.);
        my = mm2(iTime*0.05+um.y*5.);
    }
    ro.xz *= mx;rd.xz *= mx;
    ro.xy *= my;rd.xy *= my;

    vec2 t = iSphere(ro,rd);

    vec3 lightDir = normalize(vec3(1,0,1));

    // Alternate between background and sphere mapping
    vec3 color;
    if (t.x > 0.) {
        // Sphere mapping
    	vec3 normal = ro + rd * t.x;

        // Sample albedo textures
        materialValues planar = triplanar(iChannel0, normal, 0.7, CONTRAST);

        #ifdef SHOWGRID
            // Show unlit color
            color = planar.color;
        #else
            // Apply lighting to show the normals too
            color = phongLighting(planar.color, planar.normal, ro, lightDir);
        #endif       
    } else {
        // Background mapping
        vec3 normal = rd;       

        // Sample albedo textures
        materialValues planar = triplanar(iChannel1, normal, 2., CONTRAST);

        #ifdef SHOWGRID
            // Show unlit color
            color = planar.color;
        #else        
            // Make stars less bright
            float stars = pow(planar.color.x * planar.color.y * planar.color.z,2.);
            vec3 albedo = planar.color * stars; // Make stars colorful

            // Create sun with halo
            vec3 sun = sun(normal, lightDir);

            // Combine stars albedo and sunlight
            color = max(albedo - sun, 0.) + sun;
        #endif 
    }

    #ifndef SHOWGRID
        // Fresnel mask for atmosphere
        float fresnel = pow(clamp(1.-abs(t.y), 0., 1.), 32.);
        color = mix(color, vec3(0.404,0.302,0.275), fresnel);
    #endif

	fragColor = vec4(color, 1.0);
}

