/*
Welcome to a simple demonstration of a method I've devised to address 
mipmapping issues that can arise in sphere mapping. Although this method 
is straightforward and likely not groundbreaking, it cheaply solves 
the problem.

Here's how it works: we sample the partial derivatives of sphere mapping 
and a 180-degree rotation of longitudinal coordinates. Then, we  
switch between these two sets of longitudinal partial derivatives on 
opposite sides of the sphere. This elegant solution resolves mip level 
problems and eliminates flickering artifacts along the edge of sphere 
mapping, while maintaining the miplevel on the other regions. 

The left side has the broken mips at the texture edge. The right side 
is my fixed version. Feel free to explore this interactive scene using 
mouse controls to adjust your viewpoint and move the border around.

For more exploration, see my tangent space matrix for spheremaps:
https://www.shadertoy.com/view/ctXfDS
*/

// Spherical coordinate mapping (Mercator projection)
vec2 sphereMap(vec3 uvw) {
    // Mercator projection around the z-axis
    float u = 0.5 + atan(uvw.y, uvw.x) / (2.0 * 3.1415);
    float v = 0.5 + asin(uvw.z) / 3.1415;
    return vec2(u, v);
}

// Simple spheremap texture sampler with broken mips
vec4 sphereProjectionBroken(sampler2D tex, vec3 normal, float scale) {
    // Sample spheremap
    vec2 uv = sphereMap(normal);

    // Apply scale
    uv *= scale;

    // Sample texture using default mip settings, which are broken
    return texture(tex, uv);
}

// Simple spheremap texture sampler with corrected mips
vec4 sphereProjectionFixed(sampler2D tex, vec3 normal, float scale) {
    // Sample spheremap
    vec2 uv = sphereMap(normal);

    // Append 180-degree flipped uv.x
    vec3 uvn = vec3(uv, fract(uv.x - 0.5));

    // Apply scale
    uvn *= scale;

    // Calculate partial derivatives of the 3 coordinates
    vec3 duvndx = dFdx(uvn);
    vec3 duvndy = dFdy(uvn);

    // Switch partial derivatives between sphere sides
    vec2 duvdx;
    vec2 duvdy;
    if (normal.x < 0.0) {
        // Fix edge side
        duvdx = duvndx.zy;
        duvdy = duvndy.zy;
    } else {
        // Default partial derivatives
        duvdx = duvndx.xy;
        duvdy = duvndy.xy;
    }

    // Sample texture using coordinates and the compound partial derivatives
    return textureGrad(tex, uvn.xy, duvdx, duvdy);
}

// _____________________________3D Section_____________________________
// https://www.shadertoy.com/view/4sjXW1 by nimitz (twitter: @stormoid)

mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}

vec2 iSphere(in vec3 ro, in vec3 rd) {
    vec3 oc = ro;
    float b = dot(oc, rd);
    float c = dot(oc,oc) - 1.;
    float h = b*b - c;
    //if(h <0.0) return vec2(-1.);
    return vec2(-b - sqrt(h), h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {	
	vec2 p = fragCoord.xy/iResolution.xy-0.5;
	p.x*=iResolution.x/iResolution.y;
	vec2 um = iMouse.xy / iResolution.xy-.5;
	um.x *= iResolution.x/iResolution.y;
    p*= 1.5;

    //camera
	vec3 ro = vec3(0.,0.,2.4);
    vec3 rd = normalize(vec3(p,-1.5));
    mat2 mx;
    mat2 my;
    if(iMouse.x > 0.0)	{    
        mx = mm2(1.5);
        my = mm2(um.y*7.);
    } else { 
        mx = mm2(mix(1.0,2.0,sin(iTime*.5)*0.5+0.5));
        my = mm2(mix(-0.5,0.5,cos(iTime*.5)*0.5+0.5));
    }
    ro.xz *= mx;rd.xz *= mx;
    ro.xy *= my;rd.xy *= my;

    vec2 t = iSphere(ro,rd);

    // Calculate centered UV coordinates
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y + 0.5;

    // Enable mouse controls when clicked, otherwise animate
    float border = 0.5;    
    if (iMouse.x > 0.0) {
        vec2 mouse = (iMouse.xy - iResolution.xy * 0.5) / iResolution.y + 0.5;
        border = mouse.x;
    }

    // Alternate between background and sphere mapping
    vec3 colorA;
    if (t.x > 0.) {
        // Sphere mapping
        vec3 normal = ro + rd * t.x; 

        // Alternate between fixed and broken spheremaps
        vec4 projection;
        if (uv.x > border) {
            projection = sphereProjectionFixed(iChannel0, normal, 3.0);
        } else {
            projection = sphereProjectionBroken(iChannel0, normal, 3.0);
        } 

        // Latitude of the sphere projection for cosmetics
        float lat = sphereMap(normal).x;

        // Apply looping colormap (rainbow unfortunatly makes the line hard to see)
        vec3 colormap = twilight_shifted(fract(lat*2.));

        // Interpolate between Cat and Colormap
        colorA = mix(colormap, projection.xyz, projection.w);

    }
    // Background mapping
    vec3 normal = rd;

    // Latitude of the sphere projection for cosmetics
    float lat = sphereMap(normal).x;

    // Create a colorful background
    vec3 colorB = twilight_shifted(fract((1.-lat) *3.));

    // Smooth blending of ball with background
    vec3 color = mix(colorA, colorB, smoothstep(0.02, 0.0, t.y));

    // Add a dividing line
    color += step(abs(uv.x - border), 0.002);

	fragColor = vec4(color, 1.0);
}

