#define SUBSTEPS 8.0

#define COLOR_0 vec3(1.000,0.525,0.000)
#define COLOR_1 (1.0-COLOR_0)

vec2 curve(float t, float k)
{
    return vec2(sin(t) - cos(t + k), cos(3.0*t) + sin(4.0 * - k))*0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec3 store = texture(iChannel0, fragCoord/iResolution.xy, 0.1).rgb;

    for(float i = 0.0; i < SUBSTEPS; i++)
    {
        float offset = i / SUBSTEPS * max(0.0, iTimeDelta);
        float t = max(0.0001, iTime) * 0.5;
        float k = max(0.0001, iTime) * 0.25;
        float p0 = 0.3/distance(uv, curve(t + offset, k + offset));
        float p1 = 0.3/distance(uv, curve(t + offset + 0.5, k + offset + 0.5));

        vec3 col = COLOR_0 * p0 + COLOR_1 * p1;

        store = mix(col, store, pow(0.5, max(0.0, iTimeDelta) / SUBSTEPS));
    }

    fragColor = vec4(store, 1);
}

