////////////////////////////////////////////////////////////////////////////////
//
// Inverse Complex Cubic Solver, mla, 2023.
//
// Take three complex numbers and find the equation zÂ³+AzÂ²+Bz + C = 0 = f(z) of
// which they are the roots, but instead of mapping each screen position z to
// w = f(z), map w to to z = fâ»Â¹(w), ie. find z for which f(z) = w, ie. solve
// the cubic equation zÂ³+AzÂ²+Bz+C-w = 0. This is 3-valued (for the three roots),
// and we show all 3 branches overlaid (or use keys 1-3 to select 
// the branches to show - it can be difficult to interpret all three at once).
//
// The three selected roots are shown as black circles (in the z-plane, in the
// w-plane they are all mapped to zero) and can be dragged with the mouse and
// The real and imaginary axes are also shown in both z- and w-planes.
//
// <mouse>: drag roots (black circles) around
// <up/down>: zoom in/out
// '1','2','3': select branches to show
// 'a': animation mode
// 'd': use approximate derivative
// 'r': rotation
// 'x': show untransformed z-plane
// 'w': hide w-axes
// 'z': hide z-axes
//
////////////////////////////////////////////////////////////////////////////////

vec2 ccbrt(vec2 z) {
  float t = carg(z)/3.0;
  return pow(length(z),1.0/3.0)*expi(t);
}

mat3x2 solvecubic(vec2 p, vec2 q) {
  // Solve depressed cubic: xÂ³+3px+q = 0 - note factor of 3!
  // Put x = u+v and get xÂ³ = uÂ³+vÂ³+3xuv
  // Substitute in original equation and get:
  // uÂ³+vÂ³+(3uv+3p)x + q = 0
  // Now let's constrain u,v so that uv+p = 0, and then:
  // uv = -p ie. uÂ³vÂ³ = -pÂ³ and uÂ³+vÂ³ = -q
  // This gives a quadratic equation in t with roots uÂ³ and vÂ³:
  // tÂ²+qt-pÂ³ = 0 so try to solve as:
  // uÂ³ = -q + sqrt(qÂ²+4pÂ³), vÂ³ = -q - sqrt(qÂ²+4pÂ³)
  // and take cube roots to get solution x = u+v.
  //
  // HOWEVER, we can't just combine any cube roots of the
  // quadratic solutions, the condition uv+p = 0 above must also
  // hold, so what we do is take either one of the quadratic
  // solutions with its three cube roots as the three values
  // of u, and derive the corresponding v's directly as v = -p/u.
  // As with solving a real quadratic equation, it seems prudent
  // to use the quadratic root with the largest absolute magnitude,
  // and given one cube root, we can easily find the others by
  // multiplying by the cube roots of unity, Ï and ÏÂ².
  vec2 q2 = cmul(q,q);
  vec2 p3 = cmul(p,cmul(p,p));
  vec2 s = csqrt(q2+4.0*p3);
  float sign = dot(q,s) > 0.0 ? -1.0 : 1.0; // Get largest magnitude root
  vec2 u3 = 0.5*(-q+sign*s);
  vec2 u = ccbrt(u3);
  vec2 u1 = cmul(u,omega); // Get other cuberoots by multiplying by Ï and ÏÂ².
  vec2 u2 = cmul(u,omega2);
  return mat3x2(u-cdiv(p,u), u1-cdiv(p,u1), u2-cdiv(p,u2));
}

mat3x2 solvecubic(vec2 B, vec2 C, vec2 D) {
  // Solve xÂ³+BxÂ²+Cx+D = 0
  B /= 3.0; C /= 3.0;  // Simplifies sums
  vec2 B2 = cmul(B,B); // Depress with x->x+B
  // p = c-b*b
  vec2 p = C-B2;
  // q = 2.0*b*b*b - 3.0*b*c + d
  vec2 q = cmul(B,2.0*B2-3.0*C) + D;
  mat3x2 res = solvecubic(p,q);
  // Undepress by subtracting B
  return mat3x2(res[0]-B,res[1]-B,res[2]-B);
}

// d(zÂ³+AzÂ²+Bz+C)/dz = 3zÂ²+2Az+B
float dlen(vec2 A, vec2 B, vec2 z) {
  // Inverse derivative length
  vec2 w = 3.0*cmul(z,z) + 2.0*cmul(A,z) + B;
  return 1.0/length(w); // This works!
}

float axes(vec2 z,float px) {      // Draw axes
  float eps = 0.05;
  float d = min(abs(z.x),abs(z.y));
  float x = z.x-round(z.x);
  float y = z.y-round(z.y);
  d = min(d,segment(vec2(x,z.y),vec2(0,-eps),vec2(0,eps)));
  d = min(d,segment(vec2(z.x,y),vec2(-eps,0),vec2(eps,0)));
  return smoothstep(0.0,px,d);
}

vec3 draw(vec2 w, float px) {
    vec3 col1 = vec3(1,1,0);
    vec3 col2 = vec3(0,1,1);
    vec3 neutral = vec3(1,0,0);
    vec2 w0 = w;
    float px0 = px;
    if (!key(CHAR_R)) {
      float t = 0.25*iTime;
      w = cmul(w,expi(t));
    }
    px /= length(w);
    float t = atan(0.25*length(w))/PI;
    w = clog(w);
    float alpha = w.y/PI;
    vec3 basecol = alpha < 0.0 ? col1 : col2;
    vec3 color = mix(neutral,basecol,abs(alpha));
    vec2 uv = 10.0*w/PI;
    px = 10.0*px/PI;
    //px = fwidth(length(uv));// fwidth for function result
    uv = fract(uv);
    uv = min(uv,1.0-uv);
    color = mix(vec3(3.0*t),color,smoothstep(0.0,px,min(uv.x,uv.y)));
    return color;
}

void mainImage(out vec4 fragColor, vec2 fragCoord){
    setscale();
    vec2 z = map(fragCoord);
    vec2 a = getselection(0);
    vec2 b = getselection(1);
    vec2 c = getselection(2);
    float px = 2.0*scale/iResolution.y; //fwidth(length(z));
    // xÂ³+AxÂ²+Bx+C = 0
    vec2 A = -(a+b+c);
    vec2 B = cmul(a,b)+cmul(b,c)+cmul(c,a);
    vec2 C = -cmul(a,cmul(b,c));
    mat3x2 roots = solvecubic(A,B,C-z);
    vec3 dz;
    if (!key(CHAR_D)) {
      dz = vec3(dlen(A,B,roots[0]),
                dlen(A,B,roots[1]),
                dlen(A,B,roots[2]));
      dz *= 1.414;
    } else {
      float eps = 1e-3;
      mat3x2 roots2 = solvecubic(A,B,C-z-eps);
      dz = vec3(length(roots2[0]-roots[0]),
                length(roots2[1]-roots[1]),
                length(roots2[2]-roots[2]))/eps;
    }
    vec3 color = vec3(0);
    if (key(CHAR_X)) {
      color += draw(z,px);
    } else {
      int t = 0;
      if (!key(CHAR_0+1)) { t++; color += draw(roots[0],px*dz[0]); }
      if (!key(CHAR_0+2)) { t++; color += draw(roots[1],px*dz[1]); }
      if (!key(CHAR_0+3)) { t++; color += draw(roots[2],px*dz[2]); }
      if (!key(CHAR_W)) {
        if (!key(CHAR_0+1)) color = mix(vec3(0.2),color,axes(roots[0],px*dz[0]));
        if (!key(CHAR_0+2)) color = mix(vec3(0.2),color,axes(roots[1],px*dz[1]));
        if (!key(CHAR_0+3)) color = mix(vec3(0.2),color,axes(roots[2],px*dz[2]));
      }
      if (t == 2) color *= 0.7;
      if (t == 3) color *= 0.5;
    }
    roots = solvecubic(A,B,C);
    for (int i = 0; i < 3; i++) {
      vec2 w = getselection(i);
      // Draw a little circle around each root.
      vec3 pcol = vec3(0);
      //if (i == 0 && !key(CHAR_A)) pcol = vec3(1);
      color = mix(pcol,color,smoothstep(0.0,px,abs(distance(w,z)-0.05)));
    }
    if (!key(CHAR_Z)) {
      color = mix(vec3(0),color,axes(z,px));
    }
    color = pow(color,vec3(0.4545));
    if (alert) color.r = 1.0;
    fragColor = vec4(color,1.0);
}

