  (i == 0 && !key(CHAR_A)) ? 2.0*ppath(0.25*iTime) \
  : texelFetch(iChannel2,ivec2((i)+1,3),0).xy)
#define map(screenpos) (scale*(2.0*(screenpos)-iResolution.xy)/iResolution.y)

vec2 initselection(int i) {
  if (i == 0) return vec2(-sqrt(3.0),-1);
  if (i == 1) return vec2( 0,-1.618);
  if (i == 2) return vec2(-0.5,0);
  return vec2(0);
}

const float PI = 3.14159265;
const float TWOPI = 2.0*PI;

vec2 ppath(float t) {
  return vec2(cos(t),sin(0.618*t));
}

float segment(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
}

vec2 expi(float t) {
  return normalize(vec2(cos(t),sin(t)));
}

vec2 cmul(vec2 z, vec2 w) {
  return vec2(z.x*w.x-z.y*w.y,z.x*w.y+z.y*w.x);
}

vec2 cinv(vec2 z) {
  return vec2(z.x,-z.y)/dot(z,z);
}

vec2 cdiv(vec2 z1, vec2 z2) {
  return cmul(z1,cinv(z2));
}

vec2 clog(vec2 z) {
  float r2 = dot(z,z);
  return vec2(0.5*log(r2),atan(z.y,z.x));
}

float carg(vec2 z) {
  return atan(z.y,z.x);
}

vec2 csqrt(vec2 z) {
  float t = carg(z);
  return sqrt(length(z))*vec2(cos(0.5*t),sin(0.5*t));
}

// cuberoots of 1.
const vec2 omega = 0.5*vec2(-1,sqrt(3.0));
const vec2 omega2 = omega*vec2(1,-1);

// Rotate vector p by angle t.
vec2 rotate(vec2 p, float t) {
  return cmul(p,expi(t));
}

