// draw dots in place and fade old ones

const float END_VAL = 10.;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if (readKey(KEY_ENTER)) { fragColor=vec4(0); return; }
    if (int(texelFetch(iChannel2, ivec2(0,0), 0).w) != int(iResolution.x)) { fragColor=vec4(0); return; }

    ivec2 r = ivec2(iResolution.xy);

    bool alt = readKeyToggle(KEY_SPACE);
    vec4 org = max(vec4(0.),texelFetch(iChannel1, ivec2(fragCoord), 0 ) - (alt? vec4(0.15,0.15,0.15,0.) : vec4(0.3,0.3,0.25,0.)));
    float val = org.x;

    if (org.w >= END_VAL) { // keep this finished dot, unless it is one of our current start dots when reversing
        bool isInit = false;
        ivec4 inits = ivec4(texelFetch(iChannel0, ivec2(2,0), 0 ));
        for (int i=0; i<DOTS_PER_FRAME; i++) {
            if(ivec2(fragCoord)==ivec2(inits[i]%r.x,r.y-inits[i]/r.x))
                isInit = true;
        }
        fragColor = vec4(texelFetch(iChannel2, ivec2(fragCoord), 0 ).xyz, isInit ? 0. : END_VAL);
        return;
    }

    vec4 regions = texelFetch(iChannel0, ivec2(0,0), 0 ); //regions = vec4(2097151.0);
    int xd=int(iResolution.x) / NOF_REGIONS + 1;
    int xr=int(fragCoord.x) / xd;
    int yd=int(iResolution.y) / NOF_REGIONS + 1;
    int yr=int(fragCoord.y) / yd;
    int reg = xr + yr * NOF_REGIONS;
    int cp = reg / BPC;
    int bp = reg % BPC;
    if ((int(regions[cp]) & (1<<bp)) == 0) {
        fragColor = vec4(min(1.,val));
        if (readKeyToggle(KEY_D)) fragColor = vec4(0.5); // see skipped regions
        return;
    }

    float hitadd = alt? 0.6 : 1.0;
    int ended = 0;

    for (int i=3; i<NOF_DOTS+3; i++) {
        ivec2 p=ivec2(i%r.x,i/r.x); 
        vec3 pxw = texelFetch(iChannel0, p, 0 ).xyw;
        vec2 px = pxw.xy;
#ifdef LARGE_DOTS
        if (distance(px,fragCoord) < 1.) {
            if ((int(pxw.z) >> 12) >= NOF_STEPS) { if (ended==0) ended=1; if (ivec2(px) == ivec2(fragCoord)) ended = 2; }
#else
        if (ivec2(px) == ivec2(fragCoord)) {
            if ((int(pxw.z) >> 12) >= NOF_STEPS) ended = 2;
#endif
            org += hitadd; hitadd=0.;
            // break; // slows things down, a lot...
        }
    }
    if (ended > 0) { fragColor = vec4(texelFetch(iChannel2, ivec2(fragCoord), 0 ).xyz, ended == 1 ? org.w : END_VAL); return; }
	fragColor = min(vec4(1.),org);
}

