// Thanks IQ !
float sdRoundBox( in vec2 p, in vec2 b, in vec4 r ) 
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}
float soundsdf(vec3 p)
{
    p.x += .25;
    float rep = .05;
    float id = floor((p.x+rep*.5)/rep);
    p.x = mod(p.x+rep*.5,rep)-rep*.5;
    float h = texture(iChannel2, vec2(fract(id*.05),0.)).x; 
    float shape = _sqr(p.xy, vec2(.01,pow(h,3.)*.7));
    shape = max(shape, p.y);
    return shape;
}
vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.,-1.);

    //acc = _min(acc, vec2(length(p)-1., 0.));

    vec3 pw = p+vec3(-iTime, 0., iTime)*.5;
    float waterH = 
    (pow(texture(iChannel0, pw.xz*.01+iTime*.005).x, 2.)-.5)*.1
    +(pow(texture(iChannel0, pw.xz*.02+iTime*.002).x, 4.)-.5)*.05;
    //waterH *= 1.-sat(length(p.xz)-55.);
    acc = _min(acc, vec2(-p.y+waterH, 1.));

    vec3 ptrain = p;
    ptrain.xz *= r2d(.75);
    float wall = abs(ptrain.z+.5)-.1;
    float window = sdRoundBox(ptrain.xy-vec2(0.,-2.5), vec2(1.2, .75), vec4(.25));
    wall = max(wall, -window);
    acc = _min(acc, vec2(wall, 2.));

    float bars = soundsdf(ptrain*.25-vec3(0.,-.43,1.));
    bars = max(bars, abs(ptrain.z+.5+.095)-.005);
    bars = max(bars, window+.05);
    acc = _min(acc, vec2(bars, 3.));

    p.x += -iTime*.5;
    p.x *= .25;
    float mount = 
    p.y*2.+4.5
    -sin(p.x*.5)
    -sin(p.x)
    -sin(p.x*2.3)*.7
    -sin(p.x*5.)*.2
    -sin(p.x*3.)*.2
    -sin(p.x*12.)*.11
    ;
    float mountth = abs(ptrain.z-40.5)-.1;
    mount = max(-mount, mountth);
    acc = _min(acc, vec2(mount, 2.));
    //acc = _min(acc, vec2(-p.y-sat(.8-abs(ptrain.z)*.5), 3.));
    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.001, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}
vec3 accCol;
vec3 trace(vec3 ro, vec3 rd, int steps)
{
    accCol = vec3(0.);
    vec3 p = ro;
    for (int i = 0; i < steps && distance(p, ro) < 90.; ++i)
    {
        vec2 res = map(p);
        if (res.y == 3.)
        {
            //vec3(.2,1.,1.)
            accCol += sat(1.+sin(p.y*100.))*.19*vec3(1.000,0.200,0.322)*(1.-sat(res.x/.1));
            res.x += .02;
        }
        if (res.x < 0.001)
            return vec3(res.x, distance(p, ro), res.y);
        p+=rd*res.x;
    }
    return vec3(-1.);
}

vec3 rdrenv(vec3 rd)
{
    vec3 cols[4];
    cols[0] = vec3(42, 72, 88);
    cols[1] = vec3(0, 137, 138);
    cols[2] = vec3(100, 201, 135);
    cols[3] = vec3(250, 250, 110);
    float f = acos(-rd.y)/PI-.41;
    f = pow(sat(f*10.),2.);
    float fscale = f*3.;
    int cur = int(floor(fscale));
    int next = min(int(floor(fscale))+1,3);
    return mix(cols[cur]/255., cols[next]/255., fract(fscale));
}

vec3 rotateCol(vec3 col)
{
    vec3 cols[5];
    cols[0] = col;
    cols[1] = col.zxy;
    cols[2] = col.zyx;
    cols[3] = col.yxz;
    cols[4] = col.yxz;

    float f = asin(sin(iTime*.02+4.))/(PI*.5);
    f = f*.5+.5;
    float fscale = f*4.;
    int cur = int(floor(fscale));
    int next = min(int(floor(fscale))+1,4);
    return mix(cols[cur], cols[next], fract(fscale));

}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0.);

