    {
        depth = res.y;
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);

        col = n*.5+.5;
        vec3 refl = reflect(rd, n);
        if (res.z == 2. || res.z == 3.)
        {

            col = vec3(0.043,0.035,0.102);
            vec3 surftrain = vec3(1.,0.,0.);
            surftrain.xz *= r2d(.75);
            if (sat(abs(dot(surftrain, n))) < 0.9)
            {
                col = vec3(0.224,0.212,0.408);
                refl = normalize(refl + (vec3(rand(), rand(), rand())-.5)*.5);
                col += .5*pow(rdrenv(refl), vec3(2.));
            }

        }
        if (res.z == 1.)
        {
            //col = mix(vec3(0.239,0.071,0.349), vec3(1.000,0.302,0.000), sat((n.z*4.)));
            col = vec3(0.090,0.106,0.227);

            col += pow(rdrenv(refl), vec3(2.));
        }
    }
    col += acc;
    col = mix(col, mix(rdrenv(rd), vec3(1.), .3), sat(1.-exp(-depth*0.023)));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    _seed = iTime+texture(iChannel0, uv).x;
    vec2 off = .75*(vec2(rand(), rand())-.5)*2.*1./iResolution.x;
    vec3 col = rdr(uv+off);


    float t = iTime*1.;
    float daynight =  mix(1.4,1.7,sat(sin(t*.1+1.)));
    col *= daynight/(col+1.);
    col = rotateCol(col);

//col = pow(col, vec3(.4545));
    if (iFrame != 0)
        col = mix(col, texture(iChannel1, fragCoord/iResolution.xy).xyz, .9);
    fragColor = vec4(col,1.0);
}

