// "Balls In Motion 2" by dr2 - 2023
// License: Creative Commons Attribution-NonCommercial-ShareAlike 4.0

/*
  Hopefully the behavior looks natural (the physical model is used for
  studying granular flow, so minimal strange artifacts).
  Mouse does various things...

  No. 39 in "Ball Dynamics" series
    "Balls Falling"             (NsKGR3) - others listed here
    "Rock the Balls"            (7ltXWX)

  No.94 in "Physical Dynamics" series
    "Grid-Surfing Balls"        (mlVGWy) - others listed here
*/

#define AA  0  // (= 0/1) optional antialiasing

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrCylDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
vec3 OrAng (vec3 p);
vec3 OrAngT (vec3 p);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
mat3 QtToRMat (vec4 q);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);
vec4 Loadv4 (int idVar);

vec3 ltPos, vnBall, hbSize, blkAng, vnBlk;
float dstFar, hbMarg, baseRad, baseUp;
int nFrame, nBall, idObj, idBall, txOff;
const int idFrm = 1, idFrmEx = 2, idAx = 3, idObs = 4, idBas = 5, idFlr = 6;
const float pi = 3.1415927;

#define VAR_ZERO min (nFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec4 sLen;
  vec3 q;
  float dMin, d, tr, f, b;
  dMin = dstFar;
  p.y -= baseUp;
  q = abs (OrAng (p));
  sLen = vec4 (hbSize - hbMarg - 0.1, 0.);
  d = min (min (
     PrRoundBoxDf (q - sLen.wyz, sLen.xww + 0.05, 0.1),
     PrRoundBoxDf (q - sLen.xwz, sLen.wyw + 0.05, 0.1)),
     PrRoundBoxDf (q - sLen.xyw, sLen.wwz + 0.05, 0.1));
  DMIN (idFrm);
  b = length (hbSize.xy - hbMarg);
  f = 0.5 * (baseUp - 0.05);
  tr = 0.25;
  sLen.xyz = vec3 (b + 6. * tr, f - tr, hbSize.z - hbMarg + 2. * tr);
  d = PrCylDf (vec3 (q.xy, abs (q.z) - sLen.z + tr), 0.8 * tr, tr);
  DMIN (idAx);
  if (abs (q.x) < abs (q.y)) q.xy = q.yx;
  d = PrRoundBoxDf (vec3 (abs (q.x) - (hbSize.x - hbMarg - 1.9), q.yz),
     vec3 (2. - hbMarg, 0.1, hbSize.z - hbMarg - 0.3), 0.1);
  DMIN (idObs);
  q = p;
  q.xz = Rot2D (q.xz, blkAng.y);
  q.yz = Rot2D (q.yz, blkAng.x);
  d = min (PrRoundBoxDf (vec3 (q.xy, abs (q.z) - sLen.z),
     vec3 (b + 4. * tr, tr, tr) - 0.1, 0.1),
     PrRoundBoxDf (vec3 (abs (q.x) - b - 3. * tr, q.yz),
     vec3 (tr, tr, sLen.z + tr) - 0.1, 0.1));
  DMIN (idFrmEx);
  d = PrCylDf (vec3 (abs (q.x) - b - 4.5 * tr, q.yz).yzx, 0.8 * tr, tr);
  DMIN (idAx);
  q = p;
  q.xz = Rot2D (q.xz, blkAng.y);
  d = PrRoundBoxDf (vec3 (abs (q.xz) - sLen.xz, q.y + sLen.y).xzy,
     vec3 (tr, f, tr) - 0.1, 0.1);
  d = min (d, PrRoundBoxDf (vec3 (abs (q.x) - sLen.x, abs (q.y + sLen.y) - sLen.y, q.z),
     vec3 (tr, tr, sLen.z + tr) - 0.1, 0.1));
  d = min (d, PrRoundBoxDf (vec3 (q.xz, q.y + 2. * sLen.y).xzy,
     vec3 (sLen.x, tr, tr) - 0.1, 0.1));
  DMIN (idFrmEx);
  d = PrCylDf (vec3 (q.xz, q.y + 2. * sLen.y), 2. * tr, 1.4 * tr);
  DMIN (idAx);
  q = p;
  q.y -= - 2. * f - 0.05;
  d = PrRoundCylDf (q.xzy, baseRad, 0.1, 0.15);
  DMIN (idBas);
  p.y += baseUp;
  q = p;
  d = q.y;
  DMIN (idFlr);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = 1e-4 * vec2 (1., -1.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float dMax)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.03 * d, h));
    d += h;
    if (sh < 0.001 || d > dMax) break;
  }
  return 0.5 + 0.5 * sh;
}

float BallHit (vec3 ro, vec3 rd)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (txOff + 4 * n);
    if (p.w > 0.) {
      u = ro - p.xyz;
      rad = 0.46 * p.w;
      b = dot (rd, u);
      w = b * b - dot (u, u) + rad * rad;
      if (w > 0.) {
        d = - b - sqrt (w);
        if (d > 0. && d < dMin) {
          dMin = d;
          vnBall = (u + d * rd) / rad;
          idBall = n;
        }
      }
    }
  }
  return dMin;
}

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (txOff + 4 * n);
    u = ro - p.xyz;
    rad = 0.49 * p.w;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) dMin = d;
    }
  }
  return smoothstep (0., rng, dMin);
}

float BallChqr (int idBall, vec3 vnBall)
{
  vec3 u;
  u = vnBall * QtToRMat (Loadv4 (txOff + 4 * idBall + 2));
  return step (0., sign (u.y) * sign (u.z) * atan (u.x, u.y));
}

float BlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u;
  float dMin, dn, df;
  dMin = dstFar;
  v = ro / rd;
  tp = (hbSize - hbMarg - 0.04) / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  if (df > 0. && dn < df) {
    dMin = dn;
    vnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return dMin;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, rob, rdb, vn, ltDir;
  float dstBall, dstObj, db, att, nDotL, sh;
  rob = OrAng (ro - vec3 (0., baseUp, 0.));
  rdb = OrAng (rd);
  db = BlkHit (rob, rdb);
  dstBall = (db < dstFar) ? BallHit (rob, rdb) : dstFar;
  dstObj = ObjRay (ro, rd);
  if (min (dstBall, dstObj) < dstFar) {
    if (dstObj < dstBall) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      if (idObj == idFrm) col4 = vec4 (0.85, 0.8, 0.8, 0.1);
      else if (idObj == idFrmEx) col4 = vec4 (0.8, 0.8, 0.9, 0.1);
      else if (idObj == idAx) col4 = vec4 (0.8, 0.7, 0.7, 0.1);
      else if (idObj == idObs) col4 = vec4 (0.75, 0.7, 0.7, 0.1);
      else if (idObj == idBas) col4 = vec4 (0.6, 0.6, 0.65, 0.05);
      else if (idObj == idFlr) col4 = vec4 (0.6, 0.4, 0.1, 0.05);
    } else {
      ro += dstBall * rd;
      vn = OrAngT (vnBall);
      col4 = vec4 (HsvToRgb (vec3 (fract (33. * float (idBall) / float (nBall) +
         0.5 * BallChqr (idBall, vnBall)), 0.8, 1.)), 0.2);
    }
    ltDir = normalize (ltPos - ro);
    att = smoothstep (0., 0.01, dot (ltDir, normalize (ltPos)) - 0.95);
    nDotL = max (dot (vn, ltDir), 0.);
    if (dstObj < dstBall && idObj <= idObs) nDotL *= nDotL;
    sh = ObjSShadow (ro, ltDir, dstFar);
    sh = min (sh, 0.5 + 0.5 * BallHitSh (OrAng (ro - vec3 (0., baseUp, 0.)), OrAng (ltDir), 80.));
    col = att * (col4.rgb * (0.2 + 0.2 * max (dot (vn, ltDir * vec3 (-1., 1., -1.)), 0.) +
       0.8 * sh * nDotL) + col4.a * step (0.95, sh) * pow (max (0.,
       dot (ltDir, reflect (rd, vn))), 32.));
  } else {
    col = vec3 (0.);
  }
  if (db < min (dstObj, dstFar)) {
    ro = rob + db * rdb;
    if (dstObj < dstBall) col *= 0.95;
    vn = OrAngT (vnBlk);
    col = mix (col, vec3 (0.5) * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       0.1 * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.), pow (1. - abs (dot (rd, vn)), 5.));
  }
  return clamp (col, 0., 1.);
}

vec3 ShowWg (vec2 uv, vec2 canvas, vec3 col)
{
  vec4 stDat;
  vec3 wgBx;
  vec2 cVec, w;
  float mPtrz;
  int wgSel;
  wgBx = vec3 (1.4, 0., 0.25);
  stDat = Loadv4 (2);
  mPtrz = stDat.z;
  wgSel = int (stDat.w);
  stDat = Loadv4 (3);
  cVec = stDat.xy;
  w = uv -  wgBx.xy;
  col = mix (vec3 (0.35), col, 0.5 + 0.5 * smoothstep (4., 6., PrRoundBox2Df (w,
     vec2 (wgBx.z), 0.03) * canvas.y));
  col = mix (vec3 (0., 1., 1.), col, smoothstep (3., 4., abs (length (w) - wgBx.z) * canvas.y));
  if (length (w) < wgBx.z) col = mix (vec3 (0., 1., 1.), col,
       smoothstep (3., 4., Minv2 (abs (w) * canvas.y)));
  if (length (cVec) < 1.) col = mix (vec3 (1., 1., 0.), col,
     smoothstep (10., 15., length (w - cVec * wgBx.z) * canvas.y));
  return col;
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 col, ro, rd;
  vec2 canvas, uv;
  float el, az, asp, zmFac, sr;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  asp = canvas.x / canvas.y;
  txOff = 4;
  stDat = Loadv4 (0);
  hbSize = stDat.xyz;
  baseRad = 1.8 * hbSize.x;
  stDat = Loadv4 (1);
  blkAng = stDat.xyz;
  nBall = int (stDat.w);
  stDat = Loadv4 (3);
  el = stDat.z;
  az = stDat.w;
  zmFac = 6. - 5. * el;
  el = 0.2 * (el + 0.05 * pi) - 0.05 * pi;
  vuMat = StdVuMat (el, az);
  hbMarg = 0.4;
  baseUp = 1.1 * length (hbSize - hbMarg) + 0.1;
  ro = vuMat * vec3 (0., 1., - 12. * hbSize.x);
  ro.y += baseUp;
  dstFar = 20. * length (hbSize);
  ltPos = 100. * vec3 (0.4, 1., -1.);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  col = ShowWg (uv, canvas, col);
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

mat3 QtToRMat (vec4 q)
{
  mat3 m;
  vec3 a1, a2;
  a1 = q.yzx * q.zxy;
  a2 = q.w * q.xyz;
  a1 += a2;
  a2 = a1 - 2. * a2;
  m[1][2] = a1.x;  m[2][1] = a2.x;
  m[2][0] = a1.y;  m[0][2] = a2.y;
  m[0][1] = a1.z;  m[1][0] = a2.z;
  q.xyz = q.xyz * q.xyz + q.w * q.w - 0.5;
  m[0][0] = q.x;  m[1][1] = q.y;  m[2][2] = q.z;
  return 2. * m;
}

vec3 OrAng (vec3 p)
{
  p.xz = Rot2D (p.xz, blkAng.y);
  p.yz = Rot2D (p.yz, blkAng.x);
  p.xy = Rot2D (p.xy, blkAng.z);
  return p;
}

vec3 OrAngT (vec3 p)
{
  p.xy = Rot2D (p.xy, - blkAng.z);
  p.yz = Rot2D (p.yz, - blkAng.x);
  p.xz = Rot2D (p.xz, - blkAng.y);
  return p;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. -
     3.) - 1., 0., 1.), c.y);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

