  float el, az, mPtrPz, asp, diam;
  int mId, pxId, wgSel, wgReg, nbMax;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  nbEdge = ivec3 (ivec2 (16), 2);
  txOff = 4;
  nbMax = nbEdge.x * nbEdge.y * nbEdge.z;
  if (iFrag.x >= txRow || pxId >= 4 * nbMax + txOff) discard;
  nFrame = iFrame;
  canvas = iResolution.xy;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / iResolution.xy - 0.5;
  if (pxId >= txOff) mId = (pxId - txOff) / 4;
  else mId = -1;
  asp = canvas.x / canvas.y;
