  doInit = false;
  if (nFrame <= 1) doInit = true;
  dt = 0.01;
  if (doInit) {
    hbSize = 0.7 * vec3 (nbEdge) + 0.5;
    nStep = 0.;
    nBall = nbMax;
    wgSel = -1;
    el = -0.05 * pi;
    az = 0.;
    blkAng = vec3 (0.);
    cVec = vec2 (0.);
  } else {
    stDat = Loadv4 (0);
    hbSize = stDat.xyz;
    nStep = stDat.w;
    ++ nStep;
    stDat = Loadv4 (1);
    blkAng = stDat.xyz;
    nBall = int (stDat.w);
    stDat = Loadv4 (2);
    mPtrPz = stDat.z;
    wgSel = int (stDat.w);
    stDat = Loadv4 (3);
    cVec = stDat.xy;
    el = stDat.z;
    az = stDat.w;
