    blkAng.z = mod (blkAng.z + 0.0003 * pi, 2. * pi);
  }
  if (mPtr.z > 0. && wgSel < 0) {
    blkAng.y = -2. * pi * mPtr.x;
    el = clamp (pi * mPtr.y, -0.45 * pi, 0.03 * pi);
