/*
This shader explains how to use a single noise value to select from 4 
weights of bilinear interpolation. But the principle can be easily 
expanded to any number of weights.

This is useful for dither interpolation to cut down the 4 samples of 
bilinear interpolation to a single one. It also needs only a single
noise value, and gives a nice even pattern. 

You can enable mouse controls with a click. X controls the border and 
Y the contrast.

See here how to use dithered interpolation: 
https://www.shadertoy.com/view/dsjBzh

And here is an application in a terrain material:
https://www.shadertoy.com/view/DsVcW1
*/

// The number of previous samples looked at to blur result, with a similar 
// effect to Temporal Anti-Aliasing (TAA) which you should use instead.
#define TEMPORALBLUR 1

// Enable debug grid where the two mappings blend with each other
#define LINETHICKNESS 0.005
//#define SHOWGRID

// enable to show the alpha/weights otherwise shows textures
#define SHOWALPHA

// Generates a bilienar weights in a grid
vec4 bilinearWeights(vec2 uv) {
    // Alpha, also known as weights for bilinear interpolation
    vec2 infUV = 1.0 - uv;

    // The order here corresponds to GatherRed (which itself is arbitrary)
    float weightA = infUV.x * uv.y;
    float weightB = uv.x * uv.y;
    float weightC = uv.x * infUV.y;
    float weightD = infUV.x * infUV.y;

    // Combine alpha values
    return vec4(weightA, weightB, weightC, weightD);
}

// simple use case for bilinear interpolation
vec3 bilinearSample(vec2 uv, float contrast) {
     // generate weights or alpha for interpolation
    vec4 weights = bilinearWeights(uv);

    // Increase contrast of alpha (power scale: 1. no contrast, 50 max contrast)
    weights = smoothContrast(weights, contrast);   

    #ifdef SHOWALPHA
        // alpha as rgb which doubles up as interpolated colors
        vec3 color = weights.xyz; 
    #else 
         // scale UV coordinates, is static for this demo
        float scale = 2.;
        vec2 uvA = uv * scale;

        // generate 3 more set's of UVs with arbitrary rotations
        vec2 uvB = vec2(-uvA.y, uvA.x); // rotated 90 deg
        vec2 uvC = vec2(uvA.x, -uvA.y); // rotated 180 deg
        vec2 uvD = vec2(uvA.y, -uvA.x); // rotated 270 deg

        // Sample texture 4 times
        vec3 colA = texture(iChannel0, uvA).xyz;
        vec3 colB = texture(iChannel0, uvB).xyz;
        vec3 colC = texture(iChannel0, uvC).xyz;
        vec3 colD = texture(iChannel0, uvD).xyz;

        // interpolate the 4 samples
        vec3 color = colA * weights.x + colB * weights.y + 
                     colC * weights.z + colD * weights.w;
    #endif

    #ifdef SHOWGRID
        // Overlaying the edges of the alpha mask
        vec4 mask = step(abs(weights - 0.1), vec4(LINETHICKNESS));
        color = max(mask.xyz, color * (1.0 - mask.w));
    #endif    
    return color;
}

// simple use case for dithered bilinear interpolation
vec3 ditheredSample(vec2 uv, vec2 fragCoord, float time, float contrast) {        
    // scale UV coordinates, is static for this demo
    float scale = 2.;
    vec2 uvA = uv * scale;

    // Calculate mip as the automatic ones don't work for dithering
    vec2 duvdx = dFdx(uvA);
    vec2 duvdy = dFdy(uvA);

    // generate weights or alpha for interpolation
    vec4 weights = bilinearWeights(uv);

    // Increase contrast of alpha (power scale: 1. no contrast, 50 max contrast)
    weights = smoothContrast(weights, contrast);   

    // Sample dither noise
    float dither = ScreenSpaceDither12(fragCoord, time);

    // Smooth contrast creates artifacts in the dither. Clamping removes them
    dither = clamp(dither, 0.01, 0.99);

    // Applies float dither to weight values.
    // The order has to be in in sync with the weights and offsets
    // The weights are grouped cumulatively, and if the dither value is within one 
    // cumulative section for one of the weights, that weight is selected
    vec2 uvMix;
    vec4 weightsDither;
    if (dither < weights.x) {
        // Weight A
        uvMix = uvA;
        weightsDither = vec4(1, 0, 0, 0);
    } else if (dither < weights.x + weights.y) {
        // Weight B
        // generate 3 more set's of UVs with arbitrary rotations
        uvMix = vec2(-uvA.y, uvA.x); // rotated 90 deg
        weightsDither = vec4(0, 1, 0, 0);
    } else if (dither < 1. - weights.w) {
        // Weight C
        // because sum of all weights is 1, so 1-w = sum of xyz
        uvMix = vec2(-uvA.x, -uvA.y); // rotated 180 deg
        weightsDither = vec4(0, 0, 1, 0);
    } else {
        // Weight D
        uvMix = vec2(uvA.y, -uvA.x); // rotated 270 deg
        weightsDither = vec4(0, 0, 0, 1);
    }

    #ifdef SHOWALPHA
        // alpha as rgb which doubles up as interpolated colors
        vec3 color = weightsDither.xyz; 
    #else 
        // Sample texture 4 times
        vec3 color = textureGrad(iChannel0, uvMix, duvdx, duvdy).xyz;        
    #endif

    #ifdef SHOWGRID
        // Overlaying the edges of the alpha mask
        vec4 mask = step(abs(weights - 0.1), vec4(LINETHICKNESS));
        color = max(mask.xyz, color * (1.0 - mask.w));
    #endif    
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {   
    // Calculate centered UV coordinates
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y + 0.5;

    // enable mouse controls when clicked, otherwise animate
    float contrast;
    float border;    
    if (iMouse.x > 0.0)	{  
        vec2 mouse = (iMouse.xy - iResolution.xy * 0.5) / iResolution.y + 0.5;
        border = mouse.x;
        contrast = mix(1., 16., pow(mouse.y,2.));
    } else {
        float myTime = iTime * 0.5;
        border = sin(myTime) * 0.5 + 0.5;
        contrast = 1.;
    }

    // Alternate between smooth alpha and dithered alpha
    vec3 color;
    if (uv.x > border) {
        // Right dithered Side
        #if TEMPORALBLUR <= 1
            // We just take a single sample from the ditheredSample function.
            color += ditheredSample(uv, fragCoord, iTime, contrast);
        #else
            // We take multiple samples from the ditheredSample function and average them.
            // This is normally done automatically by your TAA or motion blur
            for(int i = 0; i < TEMPORALBLUR; i++) {
                color += ditheredSample(uv, fragCoord, iTime - float(i), contrast);
            }
            color /= float(TEMPORALBLUR);

        #endif
    } else {
        // Left control Bilinear Side 
        color += bilinearSample(uv, contrast);
    }

    // mask values outside of 0 to 1
    vec2 mask = step(abs(uv*2.-1.), vec2(1.0));
    color *= min(mask.x, mask.y);

    // Add a dividing line
    color += step(abs(uv.x - border), 0.002);

    // Output to screen
    fragColor = vec4(color, 1.);
}

