void TraceCell(inout Ray ray, vec3 p)
{
    //load the particles 
    vec4 packed = LOAD3D(ch0, p);
    Particle p0, p1;
    unpackParticles(packed, p, p0, p1);

    if(p0.mass > 0u) iSphere(ray, vec4(p0.pos, 1.0), vec3(0.420,0.302,0.996) * length(p0.vel));
    if(p1.mass > 0u) iSphere(ray, vec4(p1.pos, 1.0), vec3(0.420,0.302,0.996) * length(p0.vel));
}

void TraceCells(inout Ray ray, vec3 p)
{
    vec3 p0 = floor(p);
    vec4 rho = LOAD3D(ch1, p);
    if(rho.z < 1e-5) return;
    range(i, -1, 1) range(j, -1, 1) range(k, -1, 1)
    {
