//from David Hoskins' "Hash without sine"
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

//get the output of a random function with a random arity
float func1(float seed, float param1, inout float arity){
    //the function is determined by a random seed

    int func_id = int(hash11(seed*2.)*8.);
    if(func_id == 0) return sin(param1);
    else if(func_id == 1){ return cos(param1); arity = 1.;}
    else if(func_id == 2){ return param1*param1; arity = 1.;}
    else if(func_id == 3){ return param1+(hash11((seed+1.)*5.-10.)); arity = 2.;}
    else if(func_id == 4){ return abs(param1); arity = 1.;}
    else if(func_id == 5){ return log(param1); arity = 1.;}
    else if(func_id == 6){ return log(param1); arity = 1.;}
    else if(func_id == 7){ return param1*(hash11((seed+1.)*5.-10.)); arity = 2.;}
    //else if(func_id == 8){ return pow(param1,(hash11((seed+1.)*5.-10.))); arity = 2.;}
    else return param1;
}

float random_function_generator(float param1,float seed){

    float arity = 1.,
    h1 = hash11(seed),
    f1 = func1((seed+h1)*2.,param1,arity);
    while(h1 > 0.25){
        seed += arity;
        h1 = hash11(seed);
        f1 = func1((seed+h1)*2.,f1,arity);
    }
    return f1;
}

