void mainImage(out vec4 fragColor, in vec2 fragCoord){
    // Initialize the Color
    vec3 color  = vec3(0.0);
    // Initialize the Coordinates
    vec2 coord = fragCoord;
    // The Super-Sampling Loop
    for(int y = -samples; y < samples; y++){for(int x = -samples; x < samples; x++){
    // Set the Super-Sampled Coordinate
    coord = (fragCoord+(vec2(x, y)/float(samples)));
    // Find The Points for a Smooth yet Aliased Line
    bool pointa =     round(function((coord.x-1.0)*xscale)*iResolution.y) >= yscale*(coord.y-0.5*iResolution.y);
    bool pointb =     round(function((coord.x    )*xscale)*iResolution.y) <= yscale*(coord.y-0.5*iResolution.y);
    bool pointc =     round(function((coord.x    )*xscale)*iResolution.y) >= yscale*(coord.y-0.5*iResolution.y);
    bool pointd =     round(function((coord.x-1.0)*xscale)*iResolution.y) <= yscale*(coord.y-0.5*iResolution.y);
    // Some Points take less than 2x to move up a pixel so we sample a point where the line is at every point
    bool pointe = abs(round(function( coord.x*xscale)*iResolution.y)-yscale*(coord.y-0.5*iResolution.y)) <= 1.0;
    // Check if the pixel is part of the line
    if((pointa && pointb) || (pointc && pointd) || pointe){
        color += vec3(1.0);
    }
    }}
    // Output the Super-Sampled Line
    fragColor = vec4(color/float(samples*samples*4), 1.0);
}

