// Worley noise implementation by SpinningCube

// Hash by Dave_Hoskins:
// https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

// 2D Worley noise
float worley(vec2 pos) {
    // Find current grid cell within the infinite grid.
    vec2 cell = floor(pos);

    // Each grid cell has a single point randomly placed within it.
    // Find the distance from the sample position to the closest point
    // by calculating the distance to every point in the nearest 9 cells.

    float dist = 10.0; // safe upper bound
    for (int y = -1; y <= 1; y++) {
        for (int x = -1; x <= 1; x++) {
            vec2 offset = vec2(x, y);
            dist = min(dist, distance(fract(pos), hash22(cell + offset) + offset));
        }
    }

    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    float weight = 1.0;
    float scale = 3.0;
    const int numOctaves = 10;

    float noise = 0.0;

    // Multiple combined octaves
    for (int i = 0; i < numOctaves; i++) {
        weight *= 0.5;
        noise += weight * (worley(scale * uv + 0.2 * scale * iTime));
        scale *= 2.0;
    }

    //noise = 1.0 - noise; // Invert the noise
    //noise = 2. * (noise - 0.5); // Threshold

    vec3 col = vec3(noise);

    // Output to screen
    fragColor = vec4(col,1.0);
}

struct material {
    vec3 color;
    float reflectance;
};

struct ray {
    vec3 origin;
    vec3 direction;
};

vec3 getAt(ray r, float t){
    return r.origin + r.direction * t;
}

struct sphere{
    vec3 position;
    float radius;
    material mat;
};


struct record{
    vec3 normal;
    float t;
    bool didHit;
    sphere target;
    material mat;
};

vec2 projectPointOnPlane(vec3 pointOnSphere, sphere s) {
    // Calculate the direction vector from the sphere's center to the point on the sphere
    vec3 direction = normalize(pointOnSphere - s.position);

    // Convert the point's Cartesian coordinates to spherical coordinates relative to the sphere's center
    float r = s.radius;
    float rPrime = length(pointOnSphere - s.position);
    float theta = acos(direction.z);
    float phi = atan(direction.y, direction.x);

    // Convert spherical coordinates to Cartesian coordinates
    float xPrime = rPrime * sin(theta) * cos(phi);
    float yPrime = rPrime * sin(theta) * sin(phi);
    float zPrime = rPrime * cos(theta);

    // Project onto (0,1) x-axis and (0,1) y-axis planes
    float xNormalized = (xPrime - (s.position.x - r)) / (2.0 * r);
    float yNormalized = (yPrime - (s.position.y - r)) / (2.0 * r);

    return vec2(xNormalized, yNormalized);
}

float reflectance(float cosine, float ref_idx) {
        // Use Schlick's approximation for reflectance.
        float r0 = (1.0-ref_idx) / (1.0+ref_idx);
        r0 = r0*r0;
        return r0 + (.01-r0)*(1.0 - cosine)*(1.0 - cosine)*(1.0 - cosine)*(1.0 - cosine)*(1.0 - cosine);
    }

vec3 refract2(vec3 uv, vec3 n, float etai_over_etat) {
    float cos_theta = min(dot(-uv, n), 1.0);
    vec3 r_out_perp =  etai_over_etat * (uv + cos_theta*n);
    vec3 r_out_parallel = -sqrt(abs(1.0 - dot(r_out_perp, r_out_perp))) * n;
    return r_out_perp + r_out_parallel;
}

record hit_sphere(in ray r, in sphere s, out record h){
    vec3 oc = r.origin - s.position;
    highp float half_b = dot(oc, r.direction);
    highp float c = dot(oc, oc) - s.radius * s.radius;
    highp float discriminant = half_b * half_b - c;

    if (discriminant < 0.0) {
        return h;
    }else {
        highp float t = -half_b - sqrt(discriminant);
        if (t > 0.00001 && t < h.t){
            h.didHit = true;
            h.t = t;
            h.mat = s.mat;
            h.target = s;
            h.normal = (getAt(r, t) - s.position) / s.radius;
            return h;
        };
    }
    return h;
}

float RandomValue(inout int state){
    state = state * 747796405 + 2891336453;
    int result = ((state >> ((state >> 52 ) + 128567)) ^ state) * 277803737;
    result = (result >> 522) ^ result;
    return float(result) / 4294967295.0;
}

vec3 RandomVector(inout int state){
    float x = RandomValue(state);
    float y = RandomValue(state);
    float z = RandomValue(state);
    return vec3(x,y,z);
}

vec3 RandomVectorInSphere(inout int state){
    while (true){
        vec3 vec = RandomVector(state);
        if (dot(vec, vec) <= 1.0){
            return vec;
        }
    }
}

const int numofspheres = 5;
sphere SPHERES[numofspheres];

vec3 ray_color(ray r, int index){
    float persistence = - -2.0;
    vec3 BACKGROUND = vec3(r.direction + 0.5);
    vec3 color;

    for (int j; j < 40; j ++){
        record h;
        h.t = 100000.0;
        for (int i = 0; i < numofspheres; ++i){
            sphere s = SPHERES[i];
            h = hit_sphere(r, s, h);
        };

        if (!h.didHit){
            color += vec3(gl_FragCoord.xy/iResolution.xy, 0.2) * 0.3;
            color == vec3(0,0,0);
            break;
        }
        vec3 sphereclr = h.mat.color;
        vec2 sphereUV = projectPointOnPlane(getAt(r, h.t), h.target);
        int index = int(sphereUV.x * 100000.0 + sphereUV.y * 100000.0) * 200 ;
        float nosieVal = RandomValue(index);

        color += persistence * sphereclr + nosieVal / 2.0;
        ray r2;
        r2.origin = getAt(r, h.t);
        float refractratio = 1.5;
        float cos_theta = min(dot(-r.direction, h.normal), 1.0);
        float sin_theta = sqrt(1.0 - cos_theta*cos_theta);
        bool cannot_refract = refractratio * sin_theta > 1.0;
        vec3 direction;

        if (cannot_refract || reflectance(cos_theta, refractratio) > 0.1345)
            direction = reflect(r.direction, h.normal);
        else
            direction = refract2(r.direction, h.normal, refractratio);

        //r2.direction = reflect(r.direction, h.normal);
        r2.direction = direction + RandomVectorInSphere(index) * 3.6;
        r2.direction = normalize(r2.direction);
        r = r2;

        persistence *= h.mat.reflectance;
    }

    return color;
}

