// Microfaceted normal distribution function.
float D_GGX(float NoH, float roughness) {
    float alpha = pow(roughness, 4.);
    float b = (NoH*NoH*(alpha - 1.) + 1.);
    return alpha/(3.14159265*b*b);
}

// Surface geometry function.
float G1_GGX_Schlick(float NoV, float roughness) {
    //float r = roughness; // original
    float r = .5 + .5*roughness; // Disney remapping.
    float k = (r*r)/2.;
    float denom = NoV*(1. - k) + k;
    return max(NoV, .001)/denom;
}

float G_Smith(float NoV, float NoL, float roughness) {
    float g1_l = G1_GGX_Schlick(NoL, roughness);
    float g1_v = G1_GGX_Schlick(NoV, roughness);
    return g1_l*g1_v;
}

// Bidirectional Reflectance Distribution Function (BRDF). 
//
// If you want a quick crash course in BRDF, see the following:
// Microfacet BRDF: Theory and Implementation of Basic PBR Materials
// https://www.youtube.com/watch?v=gya7x9H3mV0&t=730s
//
vec3 BRDF(vec3 col, vec3 n, vec3 l, vec3 v, 
          float type, float rough, float fresRef){

  vec3 h = normalize(v + l); // Half vector.

  // Standard BRDF dot product calculations.
  float nv = clamp(dot(n, v), 0., 1.);
  float nl = clamp(dot(n, l), 0., 1.);
  float nh = clamp(dot(n, h), 0., 1.);
  float vh = clamp(dot(v, h), 0., 1.);  


  // Specular microfacet (Cook- Torrance) BRDF.
  //
  // F0 for dielectics in range [0., .16] 
  // Default FO is (.16 * .5^2) = .04
  // Common Fresnel values, F(0), or F0 here.
  // Water: .02, Plastic: .05, Glass: .08, Diamond: .17
  // Copper: vec3(.95, .64, .54), Aluminium: vec3(.91, .92, .92), Gold: vec3(1, .71, .29),
  // Silver: vec3(.95, .93, .88), Iron: vec3(.56, .57, .58).
  vec3 f0 = vec3(.16*(fresRef*fresRef)); 
  // For metals, the base color is used for F0.
  f0 = mix(f0, col, type);
  vec3 F = f0 + (1. - f0)*pow(1. - vh, 5.);  // Fresnel-Schlick reflected light term.
  // Microfacet distribution... Most dominant term.
  float D = D_GGX(nh, rough); 
  // Geometry self shadowing term.
  float G = G_Smith(nv, nl, rough); 
  // Combining the terms above.
  vec3 spec = F*D*G/(4.*max(nv, .001));


  // Diffuse calculations.
  vec3 diff = vec3(nl);
  diff *= 1. - F; // If not specular, use as diffuse (optional).
  diff *= (1. - type); // No diffuse for metals.


  // Combining diffuse and specular.
  // You could specify a specular color, multiply it by the base
  // color, or multiply by a constant. It's up to you.
  return (col*diff + spec*3.14159265);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

	// Screen coordinates.
	vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

	// Camera Setup.
	vec3 camPos = vec3(0, 0, iTime*5.); // Camera position, doubling as the ray origin.
    vec3 lookAt = camPos + vec3(0, 0, .25);  // "Look At" position.

    // Light positioning. 
 	vec3 lightPos = camPos + vec3(0, .5, 6);// Put it in front of the camera.

	// Using the Z-value to perturb the XY-plane.
	// Sending the camera, "look at," and two light vectors down the tunnel. The "path" 
    // function is synchronized with the distance function.
	lookAt.xy += path(lookAt.z);
	camPos.xy += path(camPos.z);
	lightPos.xy += path(lightPos.z);

    // Using the above to produce the unit ray-direction vector.
    float FOV = PI/3.; // FOV - Field of view.
    vec3 forward = normalize(lookAt - camPos);
    vec3 right = normalize(vec3(forward.z, 0., -forward.x )); 
    vec3 up = cross(forward, right);

    // rd - Ray direction.
    vec3 rd = normalize(uv.x*right + uv.y*up + forward/FOV);
    // Fisheye lens.
    //rd = normalize(vec3(rd.xy, rd.z - dot(rd.xy, rd.xy)*.2));    

    // Swiveling the camera about the XY-plane (from left to right) when turning corners.
    // Naturally, it's synchronized with the path in some kind of way.
	rd.xy = rot2( path(lookAt.z).x/24.)*rd.xy;


    // Scene distance.
	float t = trace(camPos, rd);

    // Object ID: Back plane (0), or the metaballs (1).
    int svObjID = objID;

    // Saving some 3D cell values. In this case, distance and ID.
    vec4 svVal = gVal;


    // Background fog color.
    vec3 fog = mix(vec3(.32, .28, .16)*3., vec3(.32, .12, .08)*2., -rd.y*.5 + .5);
    //vec3 fog = vec3(0);

    // Initialize the scene color.
    vec3 sceneCol = fog;


	// The ray has effectively hit the surface, so light it up.
	if(t<FAR && svObjID>0){


    	// Surface position and surface normal.
	    vec3 sp = camPos + rd*t;

        // Voxel normal.
        vec3 sn = getNormal(sp, t);

        // Sometimes, it's necessary to save a copy of the unbumped normal.
        //vec3 snNoBump = sn;

        // I try to avoid it, but it's possible to do a texture bump and a function-based
        // bump in succession. It's also possible to roll them into one, but I wanted
        // the separation... Can't remember why, but it's more readable anyway.
        //
        // Texture scale factor.
        const float tSize0 = 1./3.;
        // Texture-based bump mapping.
	    //sn = doBumpMap(iChannel0, sp*tSize0, sn, .003);

        // Function based bump mapping. Comment it out to see the under layer. It's pretty
        // comparable to regular beveled Voronoi... Close enough, anyway.
        //sn = doBumpMap(sp, sn, .1);


	    // Ambient occlusion.
	    float ao = calcAO(sp, sn) ;//calculateAO(sp, sn);//*.75 + .25;


    	// Light direction vectors.
	    vec3 ld = lightPos - sp;

        // Distance from respective lights to the surface point.
	    float lDist = max(length(ld), .0001);

    	// Normalize the light direction vectors.
	    ld /= lDist;

	    // Light attenuation, based on the distances above.
	    float atten = 1./(1. + lDist*lDist*.05); // + distlpsp*distlpsp*0.025

        // Ambient light.
        // I wanted to use a little more than a constant for ambient light, but 
        // without having to resort to sophisticated methods, then I remembered 
        // Blackle's example, here:
        // Quick Lighting Tech - blackle
        // https://www.shadertoy.com/view/ttGfz1
        //float ambience = pow(length(sin(sn*2.)*.45 + .5)/sqrt(3.), 2.)*.75; // Studio.
        float ambience = length(sin(sn*2.)*.5 + .5)/sqrt(3.)*
                         smoothstep(-1., 1., sn.y)*.5; // Outdoor.


    	// Diffuse lighting.
	    float diff = max(dot(sn, ld), 0.);

    	// Specular lighting.
	    float spec = pow(max( dot( reflect(-ld, sn), -rd ), 0.0 ), 32.);


	    // Fresnel term. Good for giving a surface a bit of a reflective glow.
        float fre = pow( clamp(dot(sn, rd) + 1., .0, 1.), 1.);

        // Obtaining the texel color. 
        vec3 ref = reflect(sn, rd);

        // Object texturing.

        // Coloring.
        float rnd = hash31(svVal.yzw);
	    vec3 texCol = .5 + .45*cos(6.2831*rnd/3. + vec3(0, 1, 2) + .5);

        texCol = vec3(1)*dot(texCol, vec3(.299, .587, .114));

        // Metallic box threshold.
        float met = hash31(floor(svVal.yzw/3.) + .3) - .5;
        // Metallic coloring for some boxes and the frame.
        if(met<0. || svObjID==2) texCol = vec3(.2);
        //if(svObjID==2) texCol *= vec3(1, .8, .6)*1.2;

        // Multiplying objects by respective texture colors.
        if(met<0. || svObjID==2) texCol *= tex3D(iChannel1, sp/4., sn)*4.;
	    else texCol *= .1 + tex3D(iChannel0, sp*tSize0, sn)*4.;


        // Shadows.
        float shad = softShadow(sp, lightPos, sn, 8.);


        float rnd2 = hash31(svVal.yzw + .2);
        float matType = 0.; // Dielectric.
        float roughness = min(dot(texCol, vec3(.299, .587, .114))*.5, 1.);
        float reflectance = hash31(svVal.yzw)*.75;
        // Metallic properties for some boxes and the framework.
        if(met<0. || svObjID==2) { 
            texCol = texCol*2.*vec3(1, .8, .5); 
            matType = 1.; reflectance = .5; // Metallic.
            roughness = min(roughness*5., 1.);
        }


        // Requires "St Peter's Basillica" cube map loaded into "iChannel3".
        // Specular reflection.
        vec3 hv = normalize(-rd + ld); // Half vector.
        vec3 refTx = texture(iChannel3, ref, 1.).xyz; refTx *= refTx; // Cube map.
        float spRef = pow(max(dot(hv, sn), 0.), 8.); // Specular reflection.
        //spRef = mix(spRef/4., spRef, 1. - smoothstep(0., .01, d + .05));   
        float rf = 8.;//(svObjID == 1)? 8. : 1.;
        texCol += texCol*spRef*dot(refTx, vec3(.299, .587, .114))*rf;



        // Cook-Torrance based lighting.
        vec3 ct = BRDF(texCol, sn, ld, -rd, matType, roughness, reflectance);

        // Combining the ambient and microfaceted terms to form the final color:
        // None of it is technically correct, but it does the job. Note the hacky 
        // ambient shadow term. Shadows on the microfaceted metal doesn't look 
        // right without it... If an expert out there knows of simple ways to 
        // improve this, feel free to let me know. :)
        sceneCol = (texCol*ambience*(shad*.5 + .5) + ct*(shad));


	    // Shading.
        sceneCol *= atten*ao;

        // "XT95" did such a good job with the AO, that it's worth a look on its own. :)
        //sceneCol = vec3(ao); 



	}

    // Blend in a bit of fog for atmospheric effect.    
    sceneCol = mix(sceneCol, fog, smoothstep(.2, .99, t/FAR)); // exp(-.002*t*t), etc.

    // Clamp and present the badly gamma corrected pixel to the screen.
	fragColor = vec4(sqrt(clamp(sceneCol, 0., 1.)), 1.0);

}

//////////////////////////////////////////////////////////////////////
//
// Cubic Solver Test, mla 2023
//
// Testing some solvers for depressed cubics: xÂ³+px+q = 0
// 
// Implements several versions of the usual analytic solver, also 
// for comparison Kahan's semi-iterative solver, which produces pretty 
// good results across the board. dcubic1 is a fairly standard trigonometric
// solver. dcubic2 and dcubic3 use a complex cube root operation that
// can be refined with Newton-Raphson to reduce errors from trig functions.
// nrcubic is the solver from Numerical Recipes.
//
// Shader shows complex plane/âÂ² under (inverse of) map z -> z/(1-zÂ²),
// that maps the entire plane to the unit disc. Color indicates accuracy
// of solution, red most accurate, magenta least.
//
// For position pos = (x,y), roots are a, bÂ±âc, where a = x, c = y.
// For depressed cubics, sum of roots = 0, so b = -a/2.
// If y < 0 then only one real root, else three real roots (maybe coincident).

// Numeric solution shown for mouse selection (x,y) if in the image:
// p q
// error
// input roots 1-3
// found roots 1-3
// 
// 'c': don't refine complex cube root in dcubic2 & dcubic3
// 'k': use semi-iterative Kahan solver
// 'p': polish final roots with one round of Newton-Raphson
// 's': scale projection
// 't': no projection
// 'z': zoom in
//
//////////////////////////////////////////////////////////////////////

//#define DCUBIC dcubic1
#define DCUBIC dcubic2
//#define DCUBIC dcubic3
//#define DCUBIC nrcubic

// A proper sign function that returns 1 for sign(0) (even -0)
float sgn(float x) {
  if (x < 0.0) return -1.0;
  else return 1.0;
}

vec2 ccuberoot(vec2 z) {
  // Complex cube root. 
  float r = length(z);
  z /= r;
  float t = carg(z)/3.0;
  vec2 u = vec2(cos(t),sin(t));
  if (!key(CHAR_C)) {
    // Newton-Raphson refinement
    vec2 u2 = cmul(u,u);
    u -= cdiv(cmul(u,u2)-z,3.0*u2);
  }
  return cuberoot(r)*u;
}

// cuberoots of 1.
const vec2 omega = 0.5*vec2(-1,sqrt(3.0));
const vec2 omega2 = omega*vec2(1,-1);

// Standard depressed cubic solver. This works OK, but
// GPU trig functions often aren't all that accurate which
// can cause problems.
int dcubic1(float p, float q, out vec3 roots) {
  p /= 3.0;
  float h = q*q + 4.0*p*p*p;
  float r = sqrt(abs(h));
  if (h >= 0.0) {
    float u = -0.5*(q+sgn(q)*r); // Largest root of quadratic
    float t = cuberoot(u);
    roots[0] = t - p/t;
    return 1;
  } else {
    float theta = atan(r,-q); // 0 <= theta < PI
    float k = 2.0*sqrt(abs(p));
    roots = k*cos((theta+PI*vec3(0,-2,2))/3.0);
    // And we return them in order
    assert(roots[0] >= roots[1]);
    assert(roots[1] >= roots[2]);
    return 3;
  }
}

// Annotated and with a different way of doing the multiple roots
// case (uses Newton-Raphson to get more accurate complex cube root).
int dcubic2(float p, float q, out vec3 roots) {
  // Divide p immediately by 3 to make life easier later
  p /= 3.0;
  // Now we are solving xÂ³+3px+q = 0
  // Put x = u+v and get xÂ³ = uÂ³+vÂ³+3xuv
  // Substitute in original equation and get:
  // uÂ³+vÂ³+3(uv+p)x + q = 0
  // Now we seek u,v such that uv+p = 0, so then:
  // uv = -p ie. uÂ³vÂ³ = -pÂ³ and uÂ³+vÂ³ = -q
  // This gives a quadratic equation with roots u,v:
  // tÂ²+qt-pÂ³ = 0
  // Discriminant is h = qÂ²+4pÂ³
  float h = q*q+4.0*p*p*p;
  float r = sqrt(abs(h));
  if (h >= 0.0) {
    float u = 0.5*(-q-sgn(q)*r); // Largest root
    float t = cuberoot(u);
    roots[0] = t - p/t;
    return 1;
  } else {
    // Again we have v = -p/u = -pu'/|u|Â² where u' is conjugate of u
    // So given uÂ³ = 0.5(-q Â± sqrt(-(qÂ²+4pÂ³))i) = 0.5*(-q Â± ri)
    // |uÂ³|Â² = |u|Â²Â³ = 0.25(qÂ²-qÂ²-4pÂ³) = -pÂ³
    // So |u|Â² = -p, so v = u' and u = â|p|(cos Î¸ + i sin Î¸) with Î¸ = atan(Â±r,-q)/3
    // Since u,v are complex conjugates their sum is just twice their
    // real parts, and using polar coordinates, Re(r,Î¸) = r cos Î¸
    float k = sqrt(abs(p));
    float theta = atan(r,-q)/3.0;
    vec2 z = vec2(cos(theta),sin(theta));
    if (!key(CHAR_C)) {
      // Newton-Raphson to refine the complex cube root.
      // This will compensate nicely for any trig inaccuracy.
      // Want zÂ³-t = 0, and |t| = 1
      vec2 t = vec2(-q,r)/(2.0*k*k*k);
      vec2 z2 = cmul(z,z);
      z -= cdiv(cmul(z,z2)-t,3.0*z2);
    }
    float x0 = z.x, x2 = rmul(z,omega), x1 = -x0-x2;
    // Note that roots add up to 0 for reduced cubic.
    roots = 2.0*k*vec3(x0,x1,x2); // Just want the real parts here
    // Check the roots are in order
    assert(roots[0] >= roots[1]);
    assert(roots[1] >= roots[2]);
    return 3;
  }
}

// Super simple solution, just to illustrate the idea.
// Actually, this one produces pretty good results, though since
// it's more complex arithmetic, it might be a little slower
int dcubic3(float p, float q, out vec3 roots) {
  p /= 3.0;
  float B = q, C = -p*p*p;
  float D = B*B-4.0*C;
  if (D > 0.0) {
    // The 2 complex cube roots don't lead to real solutions
    float u = cuberoot(0.5*(-B-sgn(B)*sqrt(D)));
    roots[0] = u-p/u;
    return 1;
  } else {
   // But here, 3 complex cube roots all lead to real solutions
    vec2 u0 = ccuberoot(0.5*vec2(-B,sqrt(-D)));
    vec2 u1 = cmul(omega,u0);
    vec2 u2 = cmul(omega2,u0);
    roots.x = (u0-p*cinv(u0)).x;
    roots.y = (u1-p*cinv(u1)).x;
    roots.z = (u2-p*cinv(u2)).x;
    if (roots.y < roots.z) roots.yz = roots.zy;
    return 3;
  }
}

// Numerical Recipes algorithm for solving cubic equations.
// Single root is OK, but upper half vulnerable to
// trig inaccuracies.
int nrcubic(float p, float q, out vec3 x) {
  float b = p, c = q;
  // solve x^3 + bx + c = 0
  float Q = -b/3.0;
  float R = 0.5*c;
  float R2 = R*R, Q3 = Q*Q*Q;
  if (R2 < Q3) {
    float X = R/sqrt(Q3);
    float theta = acos(X);
    float S = sqrt(Q); // Q must be positive since 0 <= R2 < Q3
    x[0] = -2.0*S*cos(theta/3.0);
    x[1] = -2.0*S*cos((theta+2.0*PI)/3.0);
    x[2] = -2.0*S*cos((theta+4.0*PI)/3.0);
    return 3;
  } else {
    float A = -sgn(R)*pow(abs(R)+sqrt(R2-Q3),1.0/3.0);
    float B = A == 0.0 ? 0.0 : Q/A;
    x[0] = A+B;
    return 1;
  }
}

float cbrt(float x) {
  return sign(x)*pow(abs(x),1.0/3.0);
}

// The Kahan cubic algorithm, for explanation see:
// https://people.eecs.berkeley.edu/~wkahan/Math128/Cubic.pdf

int quadratic(float A, float B, float C, out vec2 res) {
  float b = -0.5*B, b2 = b*b;
  float q = b2 - A*C;
  if (q < 0.0) return 0;
  float r = b + sgn(b)*sqrt(q);
  if (r == 0.0) {
    res[0] = C/A;
    res[1] = -res[0];
  } else {
    res[0] = C/r;
    res[1] = r/A;
  }
  return 2;
}

void eval(float X, float A, float B, float C, float D,
          out float Q, out float Q1, out float B1,out float C2) {
  float q0 = A*X;
  B1 = q0+B;
  C2 = B1*X+C;
  Q1 = (q0+B1)*X + C2;
  Q = C2*X + D;
}

// Solve: Ax^3 + Bx^2 + Cx + D == 0
// Find one real root, then reduce to quadratic.
int kahansolve(float C, float D, out vec3 res) {
  float A = 1.0, B = 0.0;
  float X,b1,c2;
  //assert(abs(D) > 1e-6);
  if (A == 0.0) {
    X = 1e8; A = B; b1 = C; c2 = D;
  } else if (D == 0.0) {
    X = 0.0; b1 = B; c2 = C;
  } else {
    X = -(B/A)/3.0;
    float t,r,s,q,dq,x0;
    eval(X,A,B,C,D,q,dq,b1,c2);
    t = q/A; r = pow(abs(t),1.0/3.0); s = sgn(t);
    t = -dq/A; if (t > 0.0) r = 1.324718*max(r,sqrt(t));
    x0 = X - s*r;
    if (x0 != X) {
      // 5 iterations does the job
      for (int i = 0; i < 5; i++) {
        X = x0;
        // We only need b1,c2 at the end for deflation
        eval(X,A,B,C,D,q,dq,b1,c2);
        if (dq == 0.0) break;
        x0 -= (q/dq);
      }
      eval(X,A,B,C,D,q,dq,b1,c2); // Get c1, c2
      if (abs(A)*X*X > abs(D/X)) {
        c2 = -D/X; b1 = (c2 - C)/X;
      }
    }
  }
  res.x = X;
  return 1 + quadratic(A,b1,c2,res.yz);
}

float error(float x, float y) {
  return abs(x-y)/(abs(x)+abs(y));
}

vec3 sort(vec3 a) {
  if (a.y > a.x) a.xy = a.yx;
  if (a.z > a.y) a.yz = a.zy;
  if (a.y > a.x) a.xy = a.yx;
  return a;
}

float polish(float x, float p, float q) {
  // A single round should be enough
  for (int i = 0; i < 1; i++) {
    float x2 = x*x;
    float t = x2+p;
    x -= (x*t+q)/(t+2.0*x2);
  }
  return x;
}

int dcubic(float p, float q, out vec3 roots) {
  if (key(CHAR_K)) return kahansolve(p,q,roots);
  return DCUBIC(p,q,roots);
}

int getdata(vec2 pos, out vec2 coeffs, out vec3 inroots, out vec3 roots, out float err) {
  if (pos == vec2(0)) {
    // An exact triple root generates NaN
    roots = vec3(0), err = 0.0; coeffs = vec2(0);
    return 3;
  }
  float a = pos.x;
  float b = -0.5*a;
  float c = pos.y;
  coeffs = vec2(2.0*a*b+b*b-c, -a*(b*b-c));
  float p = coeffs.x, q = coeffs.y;
  int nroots = dcubic(p,q,roots);
  assert(nroots == 1+2*int(c >= 0.0)); // This can happen, what to do?
  for (int i = 0; i < nroots; i++) {
    if (key(CHAR_P)) roots[i] = polish(roots[i],p,q);
    assert(!isnan(roots[i]));
  }
  err = 0.0;
  if (nroots == 1) {
    err = error(a,roots[0]);
    inroots.x = a;
  } else {
    vec3 test = vec3(a,b-sqrt(c),b+sqrt(c));
    test = sort(test);
    inroots = test;
    roots = sort(roots);
    err = max(err,error(test[0],roots[0]));
    err = max(err,error(test[1],roots[1]));
    err = max(err,error(test[2],roots[2]));
  }
  return nroots;
}

bool gethudval(int row, int col, vec2 pos, out float x) {
  vec2 coeffs; vec3 inroots, roots; float err;
  int nroots = getdata(pos,coeffs,inroots,roots,err);
  if (row == 0) {
    if (col == 0) { x = coeffs[0]; return true; }
    if (col == 1) { x = coeffs[1]; return true; }
  }
  if (row == 1) {
    if (col == 0) { x = err; return true; }
  }
  for (int i = 0; i < nroots; i++) {
    if (col == i && row == 2) { x = inroots[nroots-1-i]; return true; }
    if (col == i && row == 3) { x = roots[nroots-1-i]; return true; }
  }
  return false;
}

float hud(vec2 fragCoord, vec2 pos) {
  const int digits = 7, nrows = 15, ncols = 4;
  // Table structure
  vec2 cellsize = iResolution.xy/vec2(ncols,nrows);
  ivec2 cellindex = ivec2(fragCoord/cellsize);
  int column = cellindex.x;
  int row = nrows-cellindex.y-1; // Reverse order of rows
  float x;
  if (!gethudval(row,column,pos,x)) return 0.0;

  float fsize = cellsize.y; // "Font size" = line height in pixels
  vec2 p = mod(fragCoord,cellsize)/fsize;
  float uvscale = 0.5/(16.0*fsize);   // 0.5 to sharpen a little
  vec2 dx = vec2(uvscale,0), dy = vec2(0,uvscale);

  // Draw the character
  vec4 metrics = vec4(0.25,0.75,0,1);
  float charwidth = metrics.y-metrics.x;
  float colwidth = cellsize.x/fsize-1.0; // Cell width in chars, with padding
  int charpos = int(p.x/charwidth);
  vec2 uv = vec2(0);
  int c = printfloat(x,digits,charpos); // Get character at charpos, 0 if out of range
  if (c > 0) {
    float charstart = charwidth*float(charpos); // character start
    uv = char(vec2(p.x-charstart+metrics.x,p.y),c);
  }
  vec4 t = textureGrad(iChannel0,uv,dx,dy);
  if (c > 0) return t.x;
  return 0.0;  
}


bool map(inout vec2 z) {
  if (!key(CHAR_T)) {
    if (length(z) > 1.0) return false;
    // Inverse stereographic followed by gnomonic projection.
    z /= 1.0-dot(z,z);
  } else {
    z *= 2.0;
  }
  if (key(CHAR_S)) z *= exp(0.2*iTime);
  return true;
}

vec3 getcolor(vec2 pos) {
  if (!map(pos)) return vec3(1,1,0.8);
  vec2 coeffs; vec3 inroots, roots; float err;
  int nroots = getdata(pos,coeffs,inroots,roots,err);
  vec3 color = h2rgb(0.9*2.0*atan(1000.0*sqrt(err))/PI);
  return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  fragColor = vec4(0);
  vec2 pos = (2.0*fragCoord - iResolution.xy)/iResolution.y;
  vec2 mouse = vec2(0);
  bool showmouse = iMouse.x > 0.0;
  if (showmouse) {
    mouse = (2.0*iMouse.xy - iResolution.xy)/iResolution.y;
    if (key(CHAR_Z)) {
      float zoom = 4.0;
      pos = 0.25*pos + 0.75*mouse;
    }
  }
  vec3 color = getcolor(pos);
  vec2 mpos = mouse;
  if (showmouse && map(mpos)) {
    color = mix(color,vec3(0),hud(fragCoord,mpos));
  }
  if (alert) color.rb = vec2(1);
  fragColor = vec4(color,1.0);
}

