// prepare shape

vec2 iResHalf;

vec2 circle(float i, float xradius, float yradius, float epm, float tm, vec2 pos) {
    float cp = (PI*2. / float(NOF_DOTS)) * (i*epm);
    float asp = iResolution.y / iResolution.x;
    return vec2(iResHalf.x+pos.x+sin(cp)*(asp*iResolution.x*xradius), iResHalf.y+pos.y+cos(cp*tm)*(iResolution.y*yradius));
}

vec2 randbox(float i, float xradius, float yradius, float rp, vec2 pos) {
    float asp = iResolution.y / iResolution.x;
    return vec2(iResHalf.x+pos.x+(hash1(i*0.1+rp)-.5)*(asp*iResolution.x*xradius), iResHalf.y+pos.y+(hash1(i*0.1+0.11+rp)-.5)*(iResolution.y*yradius));
}

vec2 boxr(float i, float xradius, float yradius, float rp, vec2 pos) {
    float asp = iResolution.y / iResolution.x;
    if ((int(i) & 1) == 1)
        return vec2(iResHalf.x+pos.x+(hash1(i*0.1+rp)-.5)*(asp*iResolution.x*xradius), iResHalf.y+pos.y+(hash1(i*0.1+0.11+rp)>0.5?0.5:-0.5)*(iResolution.y*yradius));
    else
        return vec2(iResHalf.x+pos.x+(hash1(i*0.1+rp)>0.5?0.5:-0.5)*(asp*iResolution.x*xradius), iResHalf.y+pos.y+(hash1(i*0.1+0.11+rp)-.5)*(iResolution.y*yradius));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pxi = floor(fragCoord.y * iResolution.x + fragCoord.x - 1.);
    if (fragCoord.y > 2.5) discard;

    int frame= iFrame;
    if (readKeyToggle(KEY_ENTER)) frame += int(pxi*0.25);

    iResHalf = iResolution.xy * 0.5;
    vec2 v0 = vec2(0.);
    vec2 v1 = vec2((iResHalf.x*0.5) * (((frame/(NOF_SEQ_STEPS*NOF_ANIMS))&1)==1?1.:-1.), 0.);

    vec2 sp=v0, ep=v0;
    int animIndex = (frame / NOF_SEQ_STEPS) % NOF_ANIMS;
    switch(animIndex) {
        case 0:
            sp = circle(pxi, 0.4, 0.4,  1., 1., v0); 
            ep = circle(pxi, 0.1, 0.1,  40.5, 1., v0);
            break;
        case 1:
            sp = circle(pxi, 0.1, 0.1,  4.5, 1., v0);
            ep = circle(pxi, 0.4, 0.4,  1., 1., v0); 
            break;
        case 2:
            sp = circle(pxi, 0.4, 0.4,  1., 1., v0); 
            ep = circle(pxi, 0.0, 0.35,  125.5, 1., v1);
            break;
        case 3:
            sp = circle(pxi, 0.0, 0.35,  125.5, 1., v1);
            ep = circle(pxi, 0.65, 0.25,  1., 1., v0); 
            break;
        case 4:
            sp = circle(pxi, 0.65, 0.25,  1., 1., v0); 
            ep = randbox(pxi, 0.7, 0.7,  0., v0);
            break;
        case 5:
            sp = randbox(pxi, 0.7, 0.7,  0., v0);
            ep = circle(pxi, 0.2, 0.2,  3., 3., v0); 
            break;
        case 6:
            sp = circle(pxi, 0.2, 0.2,  3., 3., v0); 
            ep = boxr(pxi, 1.7, 0.8,  0.17, v0);
            break;
        case 7:
            sp = boxr(pxi, 1.7, 0.8,  0.17, v0);
            ep = circle(pxi, 0.2, 0.2,  3., 4., v0); 
            break;
        case 8:
            sp = circle(pxi, 0.2, 0.2,  3., 4., v0); 
            ep = circle(pxi, 0.4, 0.4,  1., 1., v0); 
            break;
    }

    float FNS = float(NOF_STEPS);

#ifdef SMOOTH_TRANSITION
    float lpsm =  min(1., float(frame % NOF_SEQ_STEPS) / FNS);
    vec2 fp = sp + ((ep-sp)/FNS) * smoothstep(0.,1.,lpsm)*FNS;
#else
    vec2 fp = sp + ((ep-sp)/FNS) * min(FNS, float(frame % NOF_SEQ_STEPS));
#endif
	fragColor = vec4(fp, 0.,0.);
}

