const int NOF_DOTS = 950;
const int NOF_STEPS = 100;
const int NOF_WAIT_STEPS = 8;
const int NOF_SEQ_STEPS = NOF_STEPS + NOF_WAIT_STEPS;
const int NOF_ANIMS = 9;

const int NOF_REGIONS = 9;
const int BPC = NOF_REGIONS * NOF_REGIONS /4 + 1;

#define SMOOTH_TRANSITION

//#define LARGE_DOTS

const float PI = 3.14159265359;

float hash1( float n ) {
    return fract(sin(n)*138.5453123);
}

const int KEY_SPACE = 32;
const int KEY_ENTER = 13;
const int KEY_D     = 68;

#define readKeyToggle(key) (texelFetch( iChannel3, ivec2(key, 2), 0).x > .5)

