//check which regions have dots

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if (fragCoord.y > 2.5) discard;

    if (ivec2(0,0)==ivec2(fragCoord)) {

        int xd=int(iResolution.x) / NOF_REGIONS + 1;
        int yd=int(iResolution.y) / NOF_REGIONS + 1;
        ivec2 r = ivec2(textureSize(iChannel0, 0));

        ivec4 regions = ivec4(0);
        for (int i=0; i<NOF_DOTS; i++) {
            ivec2 p=ivec2(i%r.x,i/r.x); 
            vec2 px = texelFetch(iChannel0, p, 0 ).xy;

            int xr=int(px.x) / xd;
            int yr=int(px.y) / yd;
            int reg = xr + yr * NOF_REGIONS;
            int cp = reg / BPC;
            int bp = reg % BPC;
            regions[cp] |= (1<<bp);
        }
        fragColor = vec4(regions);
        return;
    }

    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0 );
}

