// draw dots in place and fade old ones

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    bool alt = !readKeyToggle(KEY_SPACE);
    float val = max(0.,texelFetch(iChannel1, ivec2(fragCoord), 0 ).x - (alt? 0.02 : 0.25));

    vec4 regions = texelFetch(iChannel0, ivec2(0,0), 0 ); 
    int xd=int(iResolution.x) / NOF_REGIONS + 1;
    int xr=int(fragCoord.x) / xd;
    int yd=int(iResolution.y) / NOF_REGIONS + 1;
    int yr=int(fragCoord.y) / yd;
    int reg = xr + yr * NOF_REGIONS;
    int cp = reg / BPC;
    int bp = reg % BPC;
    if ((int(regions[cp]) & (1<<bp)) == 0) {
        fragColor = vec4(min(1.,val));
        if (readKeyToggle(KEY_D)) fragColor = vec4(0.5); // see skipped regions
        return;
    }

    ivec2 r = ivec2(textureSize(iChannel1, 0));
    float hitadd = alt? 0.33 : 1.0;

    for (int i=1; i<=NOF_DOTS; i++) {
        ivec2 p=ivec2(i%r.x,i/r.x); 
        vec2 px = texelFetch(iChannel0, p, 0 ).xy;
#ifdef LARGE_DOTS
        if (distance(px,fragCoord) < 3.0) {
#else
        if (ivec2(px) == ivec2(fragCoord)) {
#endif
            val += hitadd; hitadd=0.;
            // break; // slows things down, a lot...
        }
    }
	fragColor = vec4(min(1.,val));
}

