// Particle
struct particle_t
{
    vec2 pos;
    vec2 pos_prev;
    vec2 vel;
    float inv_mass;
    bool no_gravity;
};

// Constants
const float eps = .0001;
const float damp = .4;
const float rest_dist = .11;

// Particles
const int n_particles = 10;
particle_t p[n_particles];

// UV stuff
float px2uv;
vec2 screen_to_uv(vec2 coord)
{
    return px2uv * (coord - .5 * iResolution.xy);
}

bool is_initializing()
{
    return iFrame < 2 || iTime < .02;
}

void load_particles()
{
    for (int i = 0; i < n_particles; i++)
    {
        vec4 data = texelFetch(iChannel0, ivec2(i, 0), 0);
        p[i].pos = data.xy;
        p[i].vel = data.zw;
        p[i].inv_mass = (i == 0 ? 0. : 1.);
        p[i].no_gravity = (i == 0);
    }
}

void output_particle(in particle_t p, out vec4 frag_col)
{
    frag_col = vec4(p.pos, p.vel);
}

const int n_constraints = n_particles;
float constraint_err(int constraint_idx, out float inv_stiffness)
{
    // Distance constraints
    if (constraint_idx < (n_particles - 1))
    {
        inv_stiffness = 1. / 1000.;
        return distance(
            p[constraint_idx].pos,
            p[constraint_idx + 1].pos
        ) - rest_dist;
    }
    // Mouse constraint
    else if (constraint_idx == (n_particles - 1))
    {
        inv_stiffness = 1. / 100.;

        if (iMouse.z < .5)
            return 0.;

        vec2 uv_mouse = screen_to_uv(iMouse.xy);
        return distance(uv_mouse, p[n_particles - 1].pos);
    }
    return 0.;
}

float constraint_err(int constraint_idx)
{
    float useless;
    return constraint_err(constraint_idx, useless);
}

vec2 constraint_err_grad(int constraint_idx, int particle_idx)
{
    // Base constraint error without any modifications
    float base_err = constraint_err(constraint_idx);

    // Make a copy of the particle's position
    vec2 base_pos = p[particle_idx].pos;

    // Nudge the particle slightly in the X axis, then,
    // re-evaluate the constraint error, and take the
    // difference of the new error and the base error.
    p[particle_idx].pos = base_pos + vec2(eps, 0);
    float grad_x = constraint_err(constraint_idx) - base_err;

    // Same as above, with the Y axis
    p[particle_idx].pos = base_pos + vec2(0, eps);
    float grad_y = constraint_err(constraint_idx) - base_err;

    // Reset the particle's position
    p[particle_idx].pos = base_pos;

    // Return the gradient
    return vec2(grad_x, grad_y) / eps;
}

void solve_constraints(float dt)
{
    for (int constraint_idx = 0; constraint_idx < n_constraints; constraint_idx++)
    {
        // Calculate lambda
        float inv_stiffness;
        float numer = -constraint_err(constraint_idx, inv_stiffness);
        float denom = inv_stiffness / (dt * dt);
        for (int i = 0; i < n_particles; i++)
        {
            denom += p[i].inv_mass * len_sqr(
                constraint_err_grad(constraint_idx, i)
            );
        }
        if (denom == 0.) continue;
        float lambda = numer / denom;

        // Update the particles
        for (int i = 0; i < n_particles; i++)
        {
            p[i].pos +=
                lambda
                * p[i].inv_mass
                * constraint_err_grad(constraint_idx, i);
        }
    }
}

float sd_segment(vec2 p, vec2 a, vec2 b)
{
    // Slope and intercept
    float temp = b.x - a.x;
    if (temp == 0.) temp = 1e-9;
    float slope = (b.y - a.y) / temp;
    float intercept = a.y - slope * a.x;

    // Should we use the endpoints or the line?
    if (min(dot(p - a, b - a), dot(p - b, a - b)) < 0.)
    {
        // Distance from the endpoints
        return min(distance(p, a), distance(p, b));
    }
    else
    {
        // Distance from the line
        return abs(slope * p.x - p.y + intercept) / sqrt(slope * slope + 1.);
    }
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    // UV (2D world position of the current pixel)
    px2uv = 2. / min(iResolution.x, iResolution.y);
    vec2 uv = screen_to_uv(frag_coord);

    // Get the current values from the last frame
    load_particles();

    // Use the bottom row for data and simulation
    if (frag_coord.y < 1.)
    {
        int curr_idx = int(floor(frag_coord.x));

        if (curr_idx >= n_particles)
            return;

        // Initial values
        if (is_initializing())
        {
            particle_t p;
            p.pos = vec2(
                float(curr_idx) * rest_dist,
                .72
            );
            p.vel = vec2(0);
            output_particle(p, frag_col);

            return;
        }

        // Delta time
        float actual_dt = min(iTimeDelta, .02);

        // Substeps
        const int n_steps = 50;
        float dt = actual_dt / float(n_steps);

        // Step forward
        for (int i = 0; i < n_steps; i++)
        {
            // Iterate through the particles
            for (int j = 0; j < n_particles; j++)
            {
                // Gravity
                const vec2 gravity = vec2(0, -9.81);
                if (!p[j].no_gravity)
                    p[j].vel += dt * gravity;

                // Dampen velocity
                p[j].vel *= exp(-damp * dt);

                // Make a backup of the position
                p[j].pos_prev = p[j].pos;

                // Position
                p[j].pos += dt * p[j].vel;
            }

            // Solve constraints
            solve_constraints(dt);

            // Update the velocities
            for (int j = 0; j < n_particles; j++)
            {
                p[j].vel = (p[j].pos - p[j].pos_prev) / dt;
            }
        }

        // Output
        output_particle(p[curr_idx], frag_col);

        return;
    }

    // Background color
    vec3 col = vec3(0, .02, .07);

    // Don't render anything while initializing
    if (is_initializing())
    {
        frag_col = vec4(col, 1);
        return;
    }

    // Draw line segments connecting the particles
    {
        float min_dist = 1e9;
        for (int i = 0; i < (n_particles - 1); i++)
        {
            min_dist = min(min_dist, sd_segment(uv, p[i].pos, p[i + 1].pos));
        }

        const float thickness = .01;
        col = mix(
            col,
            vec3(1),
            .25 * remap01(min_dist, thickness, thickness - px2uv)
        );
    }

    // Draw dots on the particles
    {
        float min_dist = 1e9;
        for (int i = 0; i < n_particles; i++)
        {
            min_dist = min(min_dist, dist_sqr(uv, p[i].pos));
        }
        min_dist = sqrt(min_dist);

        const float radius = .03;
        col = mix(
            col,
            vec3(1, .75, .1),
            remap01(min_dist, radius, radius - px2uv)
        );
    }

    frag_col = vec4(col, 1);
}

