vec2 expi(float angle) {
    return vec2(cos(angle), sin(angle));
}

vec4 tex_gizmo(vec2 p, float R) {
    if (length(p) > R) { return vec4(0); }
    return vec4(1, 0, 0, 1);
}

// maps R -> ]0, 1[
float S(float x) {
    return 0.5 + 0.5 / exp(-x);
}

vec4 rgbFromHue(float H) {
    float R = abs(H * 6.0 - 3.0) - 1.0;
    float G = 2.0 - abs(H * 6.0 - 2.0);
    float B = 2.0 - abs(H * 6.0 - 4.0);

    return clamp(vec4(R, G, B, 1.0), 0.0, 1.0);
}

vec3 cone(vec2 p) {
    return vec3(p.x, p.y, length(p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float R_gizmo = 0.05;

    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 dir = normalize(p);

    // control points
    vec2 c1 = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;
    vec2 c2 = vec2(0.1, 0.5) + expi(iTime) * 0.3;
    vec2 c3 = vec2(-1.0, -0.5);

    // 3d plane through control points projected on z-cone
    vec3 p0 = cone(c1);
    vec3 b1 = cone(c2) - p0;
    vec3 b2 = cone(c3) - p0;
    vec3 normal = normalize(cross(b1, b2));

    float d = dot(cone(p), normal) - dot(p0, normal);

    float w = 0.02; // / abs(dot(normalize(cone(-p)), normalize(normal)));

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime + d + dir.xyx+vec3(0,2,4));
    //vec4 col = rgbFromHue(0.5 + 0.5*cos(iTime + d1));
    vec4 col = abs(d) > w ? rgbFromHue(mod(iTime + d, 1.)) : vec4(0);

    vec4 giz1 = tex_gizmo(p - c1, R_gizmo);
    vec4 giz2 = tex_gizmo(p - c2, R_gizmo);
    vec4 giz3 = tex_gizmo(p - c3, R_gizmo);

    // Draw gizmos on top
    fragColor = mix(mix(mix(col, giz1, giz1.a), giz2, giz2.a), giz3, giz3.a);
}

vec2 expi(float angle) {
    return vec2(cos(angle), sin(angle));
}

vec4 tex_gizmo(vec2 p, float R) {
    if (length(p) > R) { return vec4(0); }
    return vec4(1, 0, 0, 1);
}

// maps R -> ]0, 1[
float S(float x) {
    return 1. / (1. + exp(-x));
}

vec4 rgbFromHue(float H) {
    float R = abs(H * 6.0 - 3.0) - 1.0;
    float G = 2.0 - abs(H * 6.0 - 2.0);
    float B = 2.0 - abs(H * 6.0 - 4.0);

    return clamp(vec4(R, G, B, 1.0), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float R_gizmo = 0.05;

    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 dir = normalize(p);

    // control points
    vec2 c1 = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;
    vec2 c2 = vec2(0.1, 0.5) + expi(iTime) * 0.3;
    vec2 c3 = vec2(0, -0.5);

    vec2 linedir = normalize(c1 - c3);
    vec2 n = vec2(linedir.y, -linedir.x);
    float d1 = dot(p - c1, n);
    float d2 = length(p - c2);

    float ecc = d2 / abs(d1);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime + d + dir.xyx+vec3(0,2,4));
    //vec4 col = rgbFromHue(0.5 + 0.5*cos(iTime + d1));
    vec4 col = mod(15. * S(ecc), 1.) > 0.1 ? vec4(0) : rgbFromHue(mod(-iTime + S(ecc), 1.));

    vec4 giz1 = tex_gizmo(p - c1, R_gizmo);
    vec4 giz2 = tex_gizmo(p - c2, R_gizmo);
    vec4 giz3 = tex_gizmo(p - c3, R_gizmo);

    // Draw gizmos on top
    fragColor = mix(mix(mix(col, giz1, giz1.a), giz2, giz2.a), giz3, giz3.a);
}

