//speed
const float speed_x = 0.93;
const float speed_y = 0.95;
const float speed = speed_x * speed_y;
// refraction
const float emboss = 0.870;
const float intensity = 34.0;
const int steps = 10558;
const float frequency = 54.0;
const int angle = 11; // better when a prime

// reflection
const float delta = 69.1;
const float gain = 1000.596589;
const float reflectionCutOff = 0.12;
const float reflectionIntensity = 2000000000000000.;

