image_code:
////////////////////////////////////////////////////////////////////////////////
//
// Nine Point Conic and Circle. mla, 2023.
//
// Take 4 points (red, one with a cross) and the 6 lines (yellow) through
// pairs of points, forming a complete quadrangle.
//
// There are 3 diagonal points where the line through two points meets
// the line through the other two points (cyan).
//
// Given a line l, here the line at infinity, the six quadrangle lines
// meet l in six points and each point has a harmonic conjugate with
// the two points of the quadrangle on the same line (green), since
// l is at infinity, these are the midpoints of the sides.
//
// The three diagonal points and the six harmonic points, all lie on
// a conic, the nine point conic (white).
//
// The four points of the quadrangle define a pencil of conics through
// those points & the pencil contains three degenerate conics composed
// of two lines (in fact two from the six diagonals of the quadrangle).
// Furthermore, if one point of the quadrangle, p0 say, is at the 
// orthocentre of the triangle formed by the other points p1-p3, then 
// the 3 degenerate members of the pencil are orthogonal lines, and in
// fact every member of the pencil is a rectangular hyperbola (ie. 
// has orthogonal asymptotes). In this case, the nine point conic passes
// through the feet of the altitudes as well as the midpoints, and 
// it becomes the familiar nine point circle.
//
// In the shader, position the three plain red points and the yellow point
// as desired, then move the red point with the cross to the orthocentre,
// also a cross. At this point, the white conic becomes a circle and the
// grey asymptotes of the blue hyperbola become orthogonal.
//
// Use mouse to drag red and yellow points.
//
////////////////////////////////////////////////////////////////////////////////

int configuration = 2;

float lwidth = 0.007;
float pwidth = 0.03;

const vec3 pcolor0 = vec3(1,0,0);
const vec3 pcolor1 = vec3(0,1,0);
const vec3 pcolor2 = vec3(1,1,0);
const vec3 pcolor3 = vec3(0,1,1);
const vec3 pcolor4 = pcolor1;
const vec3 lcolor0 = pcolor0;
const vec3 lcolor1 = pcolor1;
const vec3 lcolor2 = vec3(1,1,0.2);
const vec3 ccolor0 = vec3(1,1,1);
const vec3 ccolor1 = vec3(0,0,1);
const vec3 ccolor2 = vec3(0.5,0.5,1);

// Solve Ax^2 + 2Bx + C = 0
bool quadratic(float A, float B, float C,
               out float x1, out float x2) {
   float D = B*B - A*C;
   if (D < 0.0) return false;
   D = sqrt(D);
   if (B < 0.0) D = -D;
   x1 = (-B-D)/A;
   x2 = C/(A*x1);
   return true;
}

// Return either intersection of lines p and q
// or line through points p and q, r = kp + jq
vec3 join(vec3 p, vec3 q) {
  return cross(p,q);
}

// Find a projective mapping taking p0,p1,p2,p4 to
// triangle of reference and unit point, ie:
// p0 -> (1,0,0), p1 -> (0,1,0), p2 -> (0,0,1), p3 -> (1,1,1)
// No three points collinear.
mat3 rproject(vec3 p0, vec3 p1, vec3 p2, vec3 p3) {
  // Just an inverse for the first three points
  // (the triangle of reference). No inverse if collinear.
  mat3 m = inverse(mat3(p0,p1,p2)); // column major!
  p3 = m*p3; 
  // Then scale each row so the unit point (1,1,1) is correct
  m = transpose(m);
  // zero components here only if not collinear
  m[0] /= p3[0];
  m[1] /= p3[1];
  m[2] /= p3[2];
  return transpose(m);
}

// Represent a projective conic as a 3x3 matrix:
//
// M = (a,d,e,
//      d,b,f,
//      e,f,c)
//
// is: axÂ² + byÂ² + czÂ² + 2(dxy + exz + fyz) = 0
// calculated as pMp for p = (x,y,z).
//
// We can treat this as a distance field, scaled by the
// (x,y) derivative in order to get correct line widths.

// With this representation, the dual conic is just the inverse;
// if the determinant is zero then there is no dual and the
// conic is degenerate.

// Construct the conic defined by 5 points.
// Method taken from "Geometry", Brannan, Esplan & Gray, CUP, 2012
mat3 conic5points(vec3 p0, vec3 p1, vec3 p2, vec3 p3, vec3 p4) {
  // p takes p0,p1,p2,p3 to triangle of reference and unit point
  mat3 p = rproject(p0,p1,p2,p3);
  // Now construct a conic through the images of p0-p4,
  p4 = p*p4;
  float a = p4.x, b = p4.y, c = p4.z;
  float d = c*(a-b);
  float e = b*(c-a);
  float f = a*(b-c);
  mat3 m = mat3(0,d,e,
                d,0,f,
                e,f,0);
  // And combine the two.
  return transpose(p)*m*p;
}

// Conic through 4 points and tangent to a line - there are 2 solutions,
// so need to solve quadratic. I'm sure I didn't invent this but
// can't remember the source.
// l.p = l'p == l'(MinvM)p = (Minv'l)'Mp
bool conic4points(vec3 p0, vec3 p1, vec3 p2, vec3 p3, vec3 l4, out mat3 M0, out mat3 M1) {
  // p takes p0,p1,p2,p3 to triangle of reference and unit point
  mat3 p = rproject(p0,p1,p2,p3);
  // and then to the square (1,1),(1,-1),(-1,1),(-1,-1)
  mat3 q = rproject(vec3(1,-1,1), vec3(-1,1,1),vec3(-1,-1,1),vec3(1,1,1));
  p = inverse(q)*p; // q is constant
  // Now construct a conic through the images of p0-p3 and l4
  l4 = transpose(inverse(p))*l4;
  float i = l4.x, j = l4.y, k = l4.z;
  float A = -j*j, B = 0.5*(i*i+j*j-k*k), C = -i*i;
  float t0,t1;
  if (!quadratic(A,B,C,t0,t1)) return false;
  M0 = mat3(t0, 0, 0,
            0, -1, 0,
            0,  0, 1.0-t0);
  M0 = transpose(p)*M0*p;
  if (isnan(determinant(M0))) return false;
  M1 = mat3(t1, 0, 0,
            0, -1, 0,
            0,  0, 1.0-t1);
  M1 = transpose(p)*M1*p;
  if (isnan(determinant(M1))) return false;
  return true;
}

// Find the two points of intersection of a line through p0 and
// p1 with the conic defined by M.
bool lineconic(vec3 p0, vec3 p1, mat3 M, out vec3 q0, out vec3 q1) {
  // (tp0 + p1)M(tp0 + p1) == 0
  float A = dot(p0,M*p0);
  float B = dot(p0,M*p1);
  float C = dot(p1,M*p1);
  float t0,t1;
  if (!quadratic(A,B,C,t0,t1)) return false;
  q0 = t0*p0+p1;
  q1 = t1*p0+p1;
  return true;
}

// Find the two points of intersection of the line l
// with the conic defined by c.
bool lineconic(vec3 l, mat3 M, out vec3 i0, out vec3 i1) {
  // Intersect l with two of the axes. This might of course
  // be the same point (1,0,0) here, so really should check
  // the third axis too.
  vec3 p0 = join(l,vec3(0,0,1));
  vec3 p1 = join(l,vec3(0,1,0));
  return lineconic(p0,p1,M,i0,i1);
}

// Harmonic conjugate of p,q and intersection of p,q with l.
vec3 harmonic(vec3 p, vec3 q, vec3 l) {
  // kp + jq is conjugate of kp - lq:
  // l.(kp - jq) = 0
  // k(l.p) = j(l.q)
  float k = dot(l,q);
  float j = dot(l,p);
  return k*p+j*q;
}

////////////////////////////////////////////////////////////////////////////////
// Drawing functions
////////////////////////////////////////////////////////////////////////////////

float px;

// Just a point
float point(vec3 pos, vec3 p) {
  //if (abs(p.z) < 1e-4) return 0.0;
  pos /= pos.z; p /= p.z; // Normalize
  float dist = distance(pos,p);
  if (isnan(dist)) return 0.0;
  return 1.0-smoothstep(-px,px,dist-pwidth);
}

float segment(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
}

// A point drawn with a cross.
float point2(vec3 pos, vec3 p) {
  pos /= pos.z; p /= p.z; // Normalize
  float t = 0.707*pwidth;
  float d = segment(pos.xy,p.xy+vec2(t,t),p.xy-vec2(t,t));
  d = min(d,segment(pos.xy,p.xy+vec2(-t,t),p.xy-vec2(-t,t)));
  if (isnan(d)) return 0.0;
  return 1.0-smoothstep(-px,px,d-0.5*lwidth);
}

float line(vec3 pos, vec3 l) {
  float dist = dot(pos,l)/(pos.z*length(l.xy));
  if (isnan(dist)) return 0.0;
  return 1.0-smoothstep(-px,px,abs(dist)-lwidth);
}

float conic(vec3 pos, mat3 m) {
  float dist = dot(pos,m*pos);   // Distance from the conic
  vec3 grad = m*pos*2.0;         // The gradient uses the same matrix.
  dist /= pos.z*length(grad.xy); // Normalize for Euclidean distance
  if (isinf(dist)) return 0.0;
  return 1.0-smoothstep(-px,px,abs(dist)-lwidth);
}

//-------------------------------------------------
//From https://www.shadertoy.com/view/XtXGRS#
vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(vec3 p) {
  float t = 0.5*iTime;
  p.x *= 1.0+0.5*sin(t);
  p.y *= 1.0+0.3*sin(0.618*t);
  p.xy = rotate(p.xy,0.2*t);
  //p.yz = rotate(p.yz,0.1*t);
  return p;
}

vec3 cmix(vec3 color0, vec3 color1, float level) {
  return mix(color0,color1,0.95*level);
}

vec2 orthocentre(vec2 p, vec2 q, vec2 r) {
  p -= r; q -= r;
  float a = dot(p,q), b = dot(p,p), c = dot(q,q);
  // (p-h).q = 0 = (q-h).p and h = up+vq
  // gives linear system
  vec2 uv = inverse(mat2(a,b,c,a))*vec2(a);
  return mat2(p,q)*uv+r;
}

// Find asymptotes of conic, ie. tangents at
// intersection with line at infinity.
// Derived from a simplification of the equation,
// putting z = 0.
bool asymptotes(mat3 s, out vec3 a0, out vec3 a1) {
  // Solve quadratic to find (real) intersections
  // with line at infinity, then take tangents.
  // Should really check for a = 0 (and b = 0)
  float a = s[0][0], b = s[1][1], d = s[0][1];
  float t = d*d-a*b;
  if (t < 0.0) return false;
  t = sqrt(t);
  a0 = s*vec3((-d+t)/a,1,0);
  a1 = s*vec3((-d-t)/a,1,0);
  return true;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  setscale();
  vec3 pos = vec3(map(fragCoord.xy),1);
  px = fwidth(pos.x);
  // p0-p3 are the quadrangle
  vec3 p0 = vec3(getselection(0),1), p1 = vec3(getselection(1),1);
  vec3 p2 = vec3(getselection(2),1), p3 = vec3(getselection(3),1);
  vec3 p4 = vec3(getselection(4),1); // Arbitrary point on pencilconic
  vec3 h = vec3(orthocentre(p1.xy,p2.xy,p3.xy),1);
  vec3 l4 = vec3(0,0,1); // line at infinity
  // pencilconic is a typical conic in the pencil through p0-p3.
  mat3 pencilconic = conic5points(p0,p1,p2,p3,p4);

  vec3 l01 = join(p0,p1);
  vec3 l02 = join(p0,p2);
  vec3 l03 = join(p0,p3);
  vec3 l12 = join(p1,p2);
  vec3 l13 = join(p1,p3);
  vec3 l23 = join(p2,p3);

  // diagonal points
  vec3 d0 = join(l01,l23);
  vec3 d1 = join(l02,l13);
  vec3 d2 = join(l03,l12);

  // harmonic points
  vec3 h01 = harmonic(p0,p1,l4);
  vec3 h02 = harmonic(p0,p2,l4);
  vec3 h03 = harmonic(p0,p3,l4);
  vec3 h12 = harmonic(p1,p2,l4);
  vec3 h13 = harmonic(p1,p3,l4);
  vec3 h23 = harmonic(p2,p3,l4);

  // conics
  mat3 ninepoint = conic5points(d0,d1,d2,h01,h23);
#if 0
  // Not needed for now
  mat3 M1,M2;
  bool doM1M2 = doM1M2 = conic4points(p0,p1,p2,p3,l4,M1,M2); // Don't need these
  vec3 i0,i1;
  bool doN = lineconic(l4,elevenpoint,i0,i1);
#endif

  vec3 tex = texture(iChannel1,0.5*fragCoord/iResolution.y).xyz;
  vec3 color = 0.1*tex;
  float t = lwidth*0.5*length(tex);
  lwidth += t;
  pwidth += t;

  // The diagonal lines of the quadrangle
  color = cmix(color,lcolor2,line(pos,l01));
  color = cmix(color,lcolor2,line(pos,l02));
  color = cmix(color,lcolor2,line(pos,l03));
  color = cmix(color,lcolor2,line(pos,l12));
  color = cmix(color,lcolor2,line(pos,l13));
  color = cmix(color,lcolor2,line(pos,l23));

  // The moving line
  //color = cmix(color,lcolor0,line(pos,l4));

  // The conics
  color = cmix(color,ccolor0,conic(pos,ninepoint));
  color = cmix(color,ccolor1,conic(pos,pencilconic));
  vec3 a0, a1;
  if (asymptotes(pencilconic,a0,a1)) {
    color = cmix(color,vec3(0.5),line(pos,a0));
    color = cmix(color,vec3(0.5),line(pos,a1));
    color = cmix(color,vec3(1),point(pos,join(a0,a1)));
  }
#if 0
  if (doM1M2) {
    color = cmix(color,ccolor2,conic(pos,M1));
    color = cmix(color,ccolor2,conic(pos,M2));
  }
#endif

  // The midpoints of the sides of the quadrangle
  color = cmix(color,pcolor1,point(pos,h01));
  color = cmix(color,pcolor1,point(pos,h02));
  color = cmix(color,pcolor1,point(pos,h03));
  color = cmix(color,pcolor1,point(pos,h12));
  color = cmix(color,pcolor1,point(pos,h13));
  color = cmix(color,pcolor1,point(pos,h23));

  // Vertices of the diagonal triangle
  color = cmix(color,pcolor3,point(pos,d0));
  color = cmix(color,pcolor3,point(pos,d1));
  color = cmix(color,pcolor3,point(pos,d2));

  // The six conjugates on each diagonal
  color = cmix(color,pcolor4,point(pos,join(l01,l4)));
  color = cmix(color,pcolor4,point(pos,join(l02,l4)));
  color = cmix(color,pcolor4,point(pos,join(l03,l4)));
  color = cmix(color,pcolor4,point(pos,join(l12,l4)));
  color = cmix(color,pcolor4,point(pos,join(l13,l4)));
  color = cmix(color,pcolor4,point(pos,join(l23,l4)));

  // The points of the quadrangle
  color = cmix(color,pcolor0,point(pos,p0));
  color = cmix(color,pcolor0,point(pos,p1));
  color = cmix(color,pcolor0,point(pos,p2));
  color = cmix(color,pcolor0,point(pos,p3));
  color = cmix(color,pcolor2,point(pos,p4));

  // Draw a cross at the orthocentre and at p0
  color = cmix(color,vec3(1),point2(pos,h));
  color = cmix(color,vec3(1),point2(pos,p0));
  fragColor = vec4(pow(1.0*color,vec3(0.4545)),1);
}

