float heart(vec2 p) {
  p.x = pabs(p.x, 0.05);

  if( p.y+p.x>1.0 )
      return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
  return sqrt(min(dot2(p-vec2(0.00,1.00)),
                  dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

float df(vec2 p) {
  vec2 hp = p;
  const float hz = 1.0;
  hp /= hz;
  hp.y -= -0.6;
  float d = heart(hp)*hz;
  return d;
}

float hf(vec2 p) {
  float d = df(p);
  float h = (-20.0*d);
  h = tanh_approx(h);
  h -= 3.0*length(p);
  h = pmin(h, 0.0, 1.);
  h *= 0.25;
  return h;
