#define ACTUAL_SIZE 64.0
#define SIZE (ACTUAL_SIZE+2.0)
#define SCALE 10.
vec2 layer_to_slice_id( float z_layer, float horizontal_slices ) {
    z_layer = floor( z_layer / 3.0 );
    float x_offset = mod( z_layer, horizontal_slices );
    float y_offset = ( z_layer - x_offset ) / horizontal_slices;
    return vec2( x_offset, y_offset );
}

float id_to_slice_layer( vec2 slice_id, float horizontal_slices ){
    return ( slice_id.y * horizontal_slices + slice_id.x ) * 3. /SIZE ;
}

vec2 fc_to_slice_coord( vec2 fc ) {
    return mod( fc, SIZE ) / SIZE;
}

vec4 t3x( sampler3D sam, vec3 uv ) {
    float textureResolution = float(textureSize(sam,0).x);
    uv = uv*textureResolution + 0.5;
    vec3 iuv = floor( uv );
    vec3 fuv = fract( uv );
    uv = iuv + fuv*fuv*(3.0-2.0*fuv);
    uv = (uv - 0.5)/textureResolution;
    return texture( sam, uv );
}

vec2 fc_to_slice_id( vec2 fc ) {
    return ( fc - mod( fc, SIZE ) ) / SIZE;
}

float sample_tex( vec3 p, sampler2D sam, float horizontal_slices, vec2 res) {
    vec3 s = mod( p, 1.0 );
    float z = s.y * SIZE;
    vec2 w = fwidth(p.xz)*2.;
    s.xz *= SIZE/(SIZE+2.0);
    s.xz += 1./SIZE;

    float blend = mod( z, 3.0 );
    int blend_id = int( blend );
    float blend_factor = fract( blend );
    vec2 id = layer_to_slice_id( z, horizontal_slices );
    vec2 fc = ( id + s.xz ) * SIZE;
    vec2 uv = fc / res;
    vec4 data = texture(sam, uv);
    float a,b;
    if ( blend_id == 0 ) {
        a = data.r;
        b = data.g;
    } else if ( blend_id == 1 ) {
        a = data.g;
        b = data.b;

    } else if ( blend_id == 2 ) {
        a = data.b;
        b = data.a;
    }
    return mix(a,b,blend_factor);
}

