#define wrap(x, _min, _max) (_min + mod(_max - _min + mod(x - _min, _max - _min), _max - _min))

float Repeat(float t, float len)
{
    //return wrap(t, 0., len);
    return clamp(t - floor(t / len) * len, 0., len);
}
vec2 Repeat(vec2 t, vec2 size)
{
    return vec2(Repeat(t.x, size.x), Repeat(t.y, size.y));
}

vec3 Repeat(vec3 t, vec3 size)
{
    return vec3(
        Repeat(t.x, size.x),
        Repeat(t.y, size.y),
        Repeat(t.z, size.z)
    );
}

// RNG
uint WangHash(uint n)
{
    // https://gist.github.com/badboy/6267743#hash-function-construction-principles
    // Wang hash: this has the property that none of the outputs will
    // collide with each other, which is important for the purposes of
    // seeding a random number generator.  This was verified empirically
    // by checking all 2^32 uints.
    n = (n ^ 61u) ^ (n >> 16);
    n *= 9u;
    n = n ^ (n >> 4);
    n *= 0x27d4eb2du;
    n = n ^ (n >> 15);

    return n;
}

uint NextState(uint state)
{
    state ^= state << 13;
    state ^= state >> 17;
    state ^= state << 5;
    return state;
}

float NextFloat(inout uint state)
{
    state = NextState(state);
    int n = int(state % 1000u);
    float t = float(n) / 1000.;   
    return t;
}

vec3 NextVec3(inout uint state)
{
    float x = NextFloat(state);
    float y = NextFloat(state);
    float z = NextFloat(state);

    return vec3(x, y, z);
}


//Convert functions based on https://www.shadertoy.com/view/XddXW7

float Convert2DTo1D(vec2 p, vec2 s) 
{
    //p = Repeat(p, s);
    p = mod(p, s);
    return floor(p.y) * s.x + p.x;
}

vec2 Convert1DTo2D(float p, vec2 size)
{
    float x = mod(p, size.x);
    float y = (p - x) / size.x + 0.5;
    return vec2(x, y);
}

float Convert3DTo1D(vec3 p, vec3 size)
{
    //p = Repeat(p, size);
    p = mod(p, size);
    return floor(p.z) * size.x * size.y + floor(p.y) * size.x + p.x;
}

vec3 Convert1DTo3D(float p, vec3 size)
{
    float x = mod(p, size.x);
    float y = mod((p - x) / size.x, size.y);
    float z = (p - x - floor(y) * size.x) / (size.x * size.y);
    return vec3(x, y + 0.5, z + 0.5);
}


//
float PeriodicalCellularNoise(vec3 p, float scale, vec3 size)
{
    p *= scale;
    vec3 mainCell = floor(p);

    float minDist = 999999.;

    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    for (int z = -1; z <= 1; z++)
    {
        vec3 cellIndex = mainCell + vec3(x, y, z);

        vec3 repeatCellIndex = Repeat(cellIndex, size * scale);
        uint seed = uint(137. + repeatCellIndex.x * 11. + repeatCellIndex.y * 17. + repeatCellIndex.z * 13.);
        uint state = WangHash(seed);

        vec3 cellPoint = cellIndex + NextVec3(state);
        vec3 diff = p - cellPoint;
        float dist = dot(diff,diff);
        minDist = min(minDist, dist);
    }

    return exp(-4.0*minDist);
}

