  s += fwd;
  t += fwd;

  s += 5. * (lattice(tt) - lattice(tt)/2.);
  t += 5. * (lattice(tt+.1) - lattice(tt+.1)/2.);


  s += gliderfbm(tt*2.)/2. - gliderfbm(tt*2.)/4.;
  t += gliderfbm(tt*2. + 11.)/2. - gliderfbm(tt*2.+11.)/4.;






  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=-normalize(cross(cz,cx));
  vec3 r=normalize(cx*uv.x+cy*uv.y+cz);


  vec3 p=s;
  float i=0.;
  float dd = 0.;
  float d;

  // march

  bool hit = false;
  for(float z=0.; z <100.; ++z) {
    i = z;
    d=abs(map(p));
    if(d<0.0001) {
      hit = true;
