#define fov 1.0

// http://www.mimirgames.com/articles/programming/digits-of-pi-needed-for-floating-point-numbers/
const float pi = 3.141592653589793;

// https://www.shadertoy.com/view/4djSRW
vec4 hash44(vec4 p4)
{
    p4 *= vec4(0.1031, 0.1030, 0.0973, 0.1099);
    p4  = fract(p4);
    p4 += dot(p4, p4.wzxy + 33.33);
    p4  = (p4.xxyz + p4.yzzw) * p4.zywx;
    p4  = fract(p4);
    return p4;
}

//
float voronoi(in vec3 p, out vec3 c)
{
    float d = 10.0;

    for(int pos_x = -1; pos_x <= 1; pos_x++) {
    for(int pos_y = -1; pos_y <= 1; pos_y++) {
    for(int pos_z = -1; pos_z <= 1; pos_z++) {
        vec4 h = hash44( vec4(vec3(pos_x, pos_y, pos_z) + floor(p), 0.0) );

        vec3 point = vec3(pos_x, pos_y, pos_z) + h.xyz;

        float t = dot( point - fract(p), point - fract(p) );

        if(t < d)
        {
            c = hash44(h.xyzw).rgb;
        }

        d = min(t, d);
    }
    }
    }

    return d;
}

//
float gradient_noise(vec3 p, float t)
{
    vec3 f = smoothstep( 0.0, 1.0, fract(p) );
    vec3 i = floor(p);

    float h0 = hash44( vec4(i + vec3(0.0, 0.0, 0.0), t) ).x;
    float h1 = hash44( vec4(i + vec3(1.0, 0.0, 0.0), t) ).x;
    float h2 = hash44( vec4(i + vec3(0.0, 1.0, 0.0), t) ).x;
    float h3 = hash44( vec4(i + vec3(1.0, 1.0, 0.0), t) ).x;
    float h4 = hash44( vec4(i + vec3(0.0, 0.0, 1.0), t) ).x;
    float h5 = hash44( vec4(i + vec3(1.0, 0.0, 1.0), t) ).x;
    float h6 = hash44( vec4(i + vec3(0.0, 1.0, 1.0), t) ).x;
    float h7 = hash44( vec4(i + vec3(1.0, 1.0, 1.0), t) ).x;

    return mix(mix(mix(h0, h1, f.x), mix(h2, h3, f.x), f.y), mix(mix(h4, h5, f.x), mix(h6, h7, f.x), f.y), f.z);
}

//
float fbm(vec3 p)
{
    float value = 0.0;
    float accum = 0.0;
    float atten = 0.5;
    float scale = 1.0;

    for(int i = 0; i < 8; i++)
    {
        value += atten * gradient_noise( scale * p, float(i) );
        accum += atten;
        atten *= 0.5;
        scale *= 2.0;
    }

    return value / accum;
}

// https://www.shadertoy.com/view/llSyRD
vec3 blackbody( float t )
{
    float h = 6.6e-34; // Planck constant
    float k = 1.4e-23; // Boltzmann constant
    float c = 3e8; // Speed of light

    vec3 w = vec3(610.0, 549.0, 468.0) / 1e9; // sRGB approximate wavelength of primaries

    // This would be more accurate if we integrate over a range of wavelengths
    // rather than a single wavelength for r, g, b

    // Planck's law https://en.wikipedia.org/wiki/Planck%27s_law

    vec3 w5 = w * w * w * w * w;    
    vec3 o = 2.0 * h * (c * c) / ( w5 * ( exp(h * c / (w * k * t) ) - 1.0) );

    return o;
}

//
vec3 sky_color(vec3 dir)
{
    const float t = 0.05;

    vec3 c;

    float v = (1.0 / t) * clamp(t - voronoi(80.0 * dir, c), 0.0, 1.0);

    // https://www.shadertoy.com/view/llSyRD
    vec3 b = 0.4 * clamp(1.0 - exp(-max(blackbody( (300.0 * c.x) + 600.0), 0.0) * 1.0), 0.0, 1.0) + 0.6;

    float n0 = fbm( 3.0 *  dir        );
    float n1 = fbm( 4.0 * (dir + 1.0) );

    float d0 = 3.0 * (max(n0 - 0.4, 0.0) + 0.07);
    float d1 = smoothstep( 0.0, 1.0, max(n1 - (4.0 * dir.y * dir.y), 0.0) );

    return ( b * v * d0 * (1.0 - d1) ) + d1;
}

//
vec2 rotate(vec2 vec, float rot)
{
    float s = sin(rot), c = cos(rot);
    return vec2(
    vec.x * c - vec.y * s,
    vec.x * s + vec.y * c
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec2 uv = 2.0 * (fragCoord - 0.5 * iResolution.xy) / max(iResolution.x, iResolution.y);

    vec3 rd = normalize( vec3(rotate(vec2(fov * uv.x, -1.0), 0.03 * pi * iTime), fov * uv.y).xzy );

    fragColor.rgb = sky_color(rd);
}

// ##### Spectral Path-Tracing #####
// Made by Jacob Bingham (Zi7ar21) on October 24, 2023
// Last Updated on October 24, 2023 at 08:37 Mountain Daylight Time

// If you found this anywhere except Shadertoy, you can find the original and possibly updated version at:
// https://www.shadertoy.com/view/DsdBWM

// ## About
// This shader path traces spheres with different Spectral Power Distributions (SPDs) and displays them
// in IEC 61966-2-1:1999 sRGB (so if your monitor is sRGB calibrated, along with sRGB gamma then this
// is really what the spectral data would look like, clamped to the sRGB gamut).
//
// ## Performance Tips
// If the shader performs poorly for you (probably integrated graphics users) try
// changing the defines at the top of the common tab. You can disable the expensive
// lookup tables.
//
// ## List of SPDs
// - The background is a flat plane with equal-energy reflectance,
// - The top sphere is an approximate tomato (yes, the fruit) SPD based on a chart I found online
// - The bottom left sphere is a 445 nm shortpass filter
// - The middle sphere is a 530 nm bandpass filter
// - The bottom right sphere is a smooth longpass filter
//
// ## Colorimetry Technical Details
// By default this shader uses a lookup table for the "CIE 1931 XYZ Colour-Matching Functions, 2-degree observer"
// and "CIE Standard Illuminant D65", and displays it in IEC 61966-2-1:1999 sRGB with a D65 white point
// and sRGB gamma (not the same as 2.2! Most displays use 2.2 now but I wanted this to be sRGB-compliant)
// along with tonemapping and undoing the sRGB transform that happens between Shadertoy and your monitor
//
// Buffer C contains the accumulated XYZ data if you want to export renders
// for evaluation in something like GIMP (use the Shadertoy EXR export in the
// bottom right of the editor while Buffer C is selected, then open the EXR
// in GIMP and tag it with a CIE 1931 XYZ color profile)
//
// Buffer D contains the untonemapped linear IEC 61966-2-1:1999 sRGB data,
// same thing with the EXR export thingy
//
// In the case of sRGB, beware of negative color values! (this is because the colors are out-of-gamut, can be
// visualized by uncommenting the dfeine below)

// Highlight sRGB out-of-gamut colors in magenta
//#define MARK_OUT_OF_GAMUT

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(texelFetch(iChannel0, ivec2(fragCoord), 0).rgb, 1.0);

    // render out-of-srgb-gamut pixels as magenta
    #ifdef MARK_OUT_OF_GAMUT
    /*
    if(fragColor.r < 0.0) { fragColor.rgb = vec3(1.0, 0.0, 1.0); return; }
    if(fragColor.g < 0.0) { fragColor.rgb = vec3(1.0, 0.0, 1.0); return; }
    if(fragColor.b < 0.0) { fragColor.rgb = vec3(1.0, 0.0, 1.0); return; }
    if(fragColor.r > 1.0) { fragColor.rgb = vec3(1.0, 0.0, 1.0); return; }
    if(fragColor.r > 1.0) { fragColor.rgb = vec3(1.0, 0.0, 1.0); return; }
    if(fragColor.r > 1.0) { fragColor.rgb = vec3(1.0, 0.0, 1.0); return; }
    */
    if(any(lessThan(fragColor.rgb,vec3(0)))||any(greaterThan(fragColor.rgb,vec3(1)))) { fragColor.rgb = vec3(1,0,1); return; }
    #endif

    fragColor.rgb *= 2.0; // exposure

    fragColor.rgb = tanh(fragColor.rgb); // tonemapping

    fragColor.rgb = clamp(fragColor.rgb, 0.0, 1.0); // clamp negative values

    fragColor.rgb = linear_to_srgb(fragColor.rgb); // undo monitor gamma
}

