//Clustering particles, with at most 2 particles per cell
//For molecular dynamics clearly 2 is not sufficient for pixel-level densities, the particle distribution degenerates into a lattice.
//SPACE to ZOOM in!
//Perhaps for fluids this might work better..?
//The density should be about 1.5x higher than in https://www.shadertoy.com/view/3s3cWr

vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

const int KEY_SPACE = 32;
bool isKeyPressed(int KEY)
{
	return texelFetch( iChannel3, ivec2(KEY,2), 0 ).x > 0.5;
}


#define radius 1.5
#define zoom 0.6

void mainImage( out vec4 col, in vec2 pos )
{    
    float prad = 1.6;
    //zoom in
    if(isKeyPressed(KEY_SPACE))
    {
    	pos = iMouse.xy + pos*zoom - R*zoom*0.5;
        prad = radius;
    }


    //compute the smoothed density
    float rho = 0.00;
    range(i, -2, 2) range(j, -2, 2)
    {
        //load the particles 
        vec2 p = floor(pos) + vec2(i, j);
        vec4 packed = LOAD(ch0, p);
        Particle p0, p1;
        unpackParticles(packed, p, p0, p1);

        //compute the density
        rho += float(p0.mass)*smoothstep(prad, 0.0, length(p0.pos - pos))/(prad*prad);
        rho += float(p1.mass)*smoothstep(prad, 0.0, length(p1.pos - pos))/(prad*prad);
    }

    col.xyz = cos(2.5*vec3(3,2,1)*clamp(rho,0.0,2.5));
}

