}

// variable SPD
float spd(float x, float lambda) {
    float sigma = 1.0/20.0;
    return exp(-pow((lambda-x)/20.0,2.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    rng_state_t rng_state;

    rng_state.s0 = uvec4(fragCoord.x, fragCoord.y, iFrame, 1)+uvec4(1);

    fragCoord = floor(fragCoord) + urand2(rng_state);

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 ro = vec3( 0.0,  0.0,  0.0);
    vec3 rd = vec3(uv.x, uv.y, -1.0);

    rd = normalize(rd);

    //float lambda = (470.0*(fragCoord.x/iResolution.x))+360.0; fragColor.xyz = cie_1931_xyz_cmf(lambda); return;
    float lambda = 470.0*urand1(rng_state)+360.0;

