#define F(n) for(int i; i < n; i++)

void mainImage( out vec4 o, vec2 u )
{
    float t, // Distance travelled
          m, // Min distance to scene
          d, // Temp distance
          f = .5;

	vec3 p, // Position
         q, // Temp position
         R = iResolution;

    // Marching loop
	F(99)
    {
        // Move in ray direction, and rotate
        //p = .8-t + t * (u+u-R.xy).xyx/R.y * vec3(f, .7, -f);  
        p = f-t + t * (vec3(u+u, 0)-R)/R.y;
        p.xz *= mat2(cos(.3*iTime+ vec4(0,11,33,0)));

        // Fun
        // p.z -= .05*cos(p.y*9.-iTime*4.);        

        // Four tetrahedron points
        mat4x3 M = -mat4x3(0, -f,  0,
                           0,  1,  -1,
                          -1,  1,  f,
                           1,  1,  f);      

        // SDF fractal loop
        F(9) 
        {
            // Ensure d<m for i=0 in loop below
            m += d;

            // Find closest distance d and tetrahedron point q
            F(4)
                d = length(p-M[i]), d<m ? m=d, q=M[i] : q;

            // Rescale and centre toward closest tetrahedron point
            p += p - q;
        }        

        if (m < 1. || t > 5.) 
            break;

        t += m / 1e3;
    }

    //o.rgb = t < 5. ? cos(p) : p/p;
    o = 0.*o + t; 
}

