#define PI  3.14159
#define PI2 6.28319
#define EPS 0.0001
const int MAX_WALKS = 20;
const int MAX_SAMPLES = 10;
// misc function 
float get_t() {
  return 20.*fract(float(iFrame)*0.005);
}

// Scene geometry
float circ_sdf(vec2 q, vec2 c, float r) {
    return length(q-c)-r;
}
float rect_sdf(vec2 q, vec2 c, vec2 halfsize) {
    vec2 d = abs(q-c)-halfsize;
    float idist = length(max(d,0.));
    float odist = min(max(d.x,d.y),0.);
    return idist+odist;
}
float union_sdf(float d1, float d2) {
    return min(d1,d2);
}
float sub_sdf(float d1, float d2) {
    return max(d1,-d2);
}
// scene sdf. 
// negative indicates outside (red).
// positive indicates inside (blue).
float main_sdf(vec2 uv) { 
    float d1 = circ_sdf(uv, vec2(.5,.6), .25);
    float d2 = rect_sdf(uv, vec2(.5,.5), vec2(0.1, 0.3));
    float eye1 = circ_sdf(uv, vec2(.58,.58), .05);
    float eye2 = circ_sdf(uv, vec2(.42,.58), .05);
    float head = sub_sdf(sub_sdf(union_sdf(d1,d2),eye1),eye2);
    // float head = union_sdf(d1,d2);
    float mouth = union_sdf(
        union_sdf(circ_sdf(uv, vec2(.5,.45), .05),
                  circ_sdf(uv, vec2(.5,.35), .05)),
        rect_sdf(uv, vec2(.5,.4), vec2(.05, 0.05))
    );
    return -sub_sdf(head, mouth);
}


// from Alex Jacobson's Curve Inflation 
// https://www.shadertoy.com/view/7tyyzW
int  seed = 1;
void srand(int s) {
    seed = s;
}
int randi(void) {
    seed = seed * 0x343fd + 0x269ec3;
    return (seed >> 16) & 32767;
}
float rand(void) {
    return float(randi())/32767.0;
}
int hash( int n ) {
	n = (n << 13) ^ n;
    return n * (n * n * 15731 + 789221) + 1376312589;
}
vec2 rand_circ(void) {
    float theta = PI2*rand();
    return vec2(cos(theta),sin(theta));
}

// green function of ball at x B(x) of radius R
// following https://dl.acm.org/doi/abs/10.1145/3386569.3392374
// Supplementary B1. This evaluate G at a random sample.
float G(float R) {
    float r = R*sqrt(rand()+0.001);
    return log(R/r)/PI2;
}
vec2 grad_G(float R) {
    float r = R*sqrt(rand());
    float theta = rand() * PI2;
    vec2 dir = vec2(cos(theta),sin(theta));
    return dir / PI2 * (1./(r*r) - 1./(R*R));
}

// Curve inflation is the solution to the 
// poisson equation: 
// âu = -4 on Î©  (f = -4) 
//  u = 0  on âÎ© (g = 0)
// return  u and grad_u.
vec3 wos(vec2 x) {
    float R = main_sdf(x);      // distance to border
    float u = 0.;               // PDE estimator 
    vec2 grad_u = vec2(0.);

    float R1 = R;
    vec2 v1;

    if (R <= 0.) return vec3(0.);       // outside scene

    for (int k = 0; k < MAX_WALKS; k++) {
        if (R < EPS) break;
        u += PI*R*R*G(R);
        // u += R*R;
        vec2 v = rand_circ();

        x += R * v;
        R = main_sdf(x);

        if (k == 0) v1 = v;
    }

    grad_u = 2./R1*u*v1;
    return vec3(u, grad_u);
}

// return color value at point with inflated height z
// https://www.shadertoy.com/view/7tyyzW
vec4 shading(vec2 uv, float u, vec2 grad_u) {    
    vec3 Kd = vec3(0.76, 0.31, 0.31); // diffuse
    vec3 Ks = vec3(1.,1.,1.);         // specular 
    vec3 Kf = 0.4*Ks;
    vec3 Ka = 0.1*Kd;               

    if(u <= 0.0) return vec4(Ka, 1.0);

    float z = sqrt(u);
    vec3 n = normalize(vec3(-grad_u*0.5/z,1));

    const int nlights = 2;
    vec3 lights[nlights];
    lights[0] = normalize(vec3(1.5,2.,1.));
    lights[1] = normalize(vec3(-1.5,2.,1.));
    vec3 Kl[nlights];
    Kl[0] = vec3(1.,0.6,0.6);
    Kl[1] = vec3(.6,0.6,1.0);
    vec3 color = vec3(0.,0.,0.);
    vec3 p = vec3(uv,z);
    vec3 c = vec3(0.,0.,5.);
    vec3 v = normalize(c-p);
    for(int i = 0;i<2;i++) {
        vec3 l = lights[i];
        float d = max(dot(n,l),0.);
        vec3 h = normalize(l+v);
        float s = pow(max(dot(n,h),0.),100.);
        color += Kl[i]*(Kd*d+Ks*s);
    }
    float NE = dot(n,v);
    float f = pow(max(sqrt(1. - NE*NE),0.0), 50.);
    color += Kf*f+Ka;

    return vec4(color,1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalize coordinate
    vec2 uv = fragCoord/iResolution.xy;
    if (iResolution.x > iResolution.y) {
        uv.x *= iResolution.x / iResolution.y;
        uv.x += .5 * (1. - iResolution.x / iResolution.y);
    } else {        
        uv.y *= iResolution.y / iResolution.x;
        uv.y += .5 * (1. - iResolution.y / iResolution.x);
    }

    // set random seed 
    ivec2 q = ivec2(fragCoord);
    srand(hash(q.x + hash(q.y + hash(iFrame))));

    // poisson solve 
    vec3 u_gradu = vec3(0.);
    for (int i = 0; i < MAX_SAMPLES; i++) {
        u_gradu += wos(uv);
    }
    u_gradu /=  float(MAX_SAMPLES);

    // rendering 
    vec4 fColor = shading(uv, u_gradu.x, u_gradu.yz);

    if(get_t() < 0.3) {
        fragColor = fColor;
    } else {
        vec4 data = texelFetch(iChannel0, ivec2(fragCoord), 0);
        fragColor = data + fColor;
    }
}

