int getBlock(int x, int y, int z)
{
    if (x < 0 || x >= SIZE)
        return 0;
    if (y < 0 || y >= SIZE)
        return 0;
    if (z < 0 || z >= SIZE)
        return 0;

    int index = (x + y * SIZE + z * SIZE * SIZE);

    return blocks[index];
}

int setBlock(int x, int y, int z, int block_id)
{
    if (x < 0 || x >= SIZE)
        return -1;
    if (y < 0 || y >= SIZE)
        return -1;
    if (z < 0 || z >= SIZE)
        return -1;

    int index = (x + y * SIZE + z * SIZE * SIZE);

    int old_id = blocks[index];

    blocks[index] = block_id;

    return old_id;
}

float intbound(float s, float ds) 
{

    if (ds < 0. && abs(round(s) - s) < 0.0001)
        return 0.;

    if (ds > 0.)
        return (1. + ceil(s) - s) / abs(ds);
    else
        return (s - floor(s)) / abs(ds);
}

vec3 intbound(vec3 s, vec3 ds)
{
    return vec3
    (
        intbound(s.x, ds.x),
        intbound(s.y, ds.y),
        intbound(s.z, ds.z)
    );
}


Result voxelTraversal(vec3 ro, vec3 rd)
{

    vec3 tMax, tDelta;
    vec3 cell = floor(ro);
    vec3 st = sign(rd);
    vec3 normal;

    tDelta = st / rd;
    tMax = intbound(ro, rd);

    cell = floor(ro);
    float depth = 0.;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 pos = ro + rd * depth;
        int block_id = getBlock(int(cell.x), int(cell.y), int(cell.z));

        if (block_id != 0)
            return Result(pos, cell, normal, depth, block_id);

        depth = min(tMax.x, min(tMax.y, tMax.z));


        if (tMax.x < tMax.y)
        {

            if (tMax.x < tMax.z)
            {
                tMax.x += tDelta.x;
                cell.x += st.x;
                normal = vec3(-st.x, 0, 0);
            }
            else 
            {
                tMax.z += tDelta.z;
                cell.z += st.z;
                normal = vec3(0, 0, -st.z);
            }
        }
        else
        {
            if (tMax.y < tMax.z)
            {
                tMax.y += tDelta.y;
                cell.y += st.y;
                normal = vec3(0, -st.y, 0);
            }
            else 
            {
                tMax.z += tDelta.z;
                cell.z += st.z;
                normal = vec3(0, 0, -st.z);
            }
        }
    }


    return Result(vec3(0), vec3(0), vec3(0), 10000000000000.0, 0);
}

