float RayMarch(vec3 ro, vec3 rd)
{

    float da = 0.0;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 ri = ro + rd * da;
        float d = GetDist(ri);
        da += d;


        if (da > MAX_DIST || d < SURF_DIST)
            break;
    }

    return da;

}

