float glider(float t) {
    float dip_interval = 20.0;
    float dip_depth = 10.0;

    // Up and down fluctuating motion
    float y_sine = 5.0 * sin(0.2 * t);

    // Time since last dip
    float time_since_last_dip = mod(t, dip_interval);

    // Modulating function that drops quickly but recovers slowly
    float modulating_func = 1.0 - sin(time_since_last_dip + 0.5 * sin(time_since_last_dip));

    // Gaussian dip function
    float y_gauss = -dip_depth * exp(-pow((time_since_last_dip - dip_interval / 2.0), 2.0) / (2.0 * pow((dip_interval / 6.0), 2.0)));

    // Continuously blend the Gaussian and sinusoidal functions, modulated by the new function
    float h = y_sine + y_gauss * modulating_func;

    // Scale the output by a factor of 3
    h /= 3.0;

    return h;
}

float gliderfbm(float t) {
  float n = 0.;
  n += glider(t);
  n += glider((t+5.12) * 2.)/2.;
  //n += glider((t+11.42) * 4.)/4.;
  return n;

}
