//Note that both of these can work with native mipmapping (no textureGrad or textureLod needed).
//Both techniques also use exclusively int hashes, which means they will perform the same way regardless of floating point
//shenanigans. For example, they'll perform the same between the GPU and the CPU. This is different from most techniques that
//rely on vec3 hash33(vec3) and such, where the result of the hash is unstable to small pertrubations.

vec2 GetFlowField(vec2 pixel_pos, vec2 focus_point)
{
    vec2 delta = pixel_pos - focus_point;
    vec2 n = delta / (length(delta) + iResolution.x / 10.0f);
    float l = length(delta) / (iResolution.x / 2.0f);
    return 
        100.0f * n +
        cos(length(delta) / (iResolution.x / 10.0f)) * vec2(-n.y, n.x) * iResolution.x / 10.0f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixel_pos = fragCoord.xy;
    vec2 pixel_velocity = GetFlowField(pixel_pos, iMouse.xy);

    vec3 linear_col = vec3(0.0f);
    vec2 moments = vec2(0.0f);

    float grid_step2 = 0.5f;
    float grid_step3 = iResolution.x / 15.0f;

    float uv_tiling = iResolution.x / 20.0f;
    float debug_pos = (sin(iTime) * 0.5f + 0.5f) * iResolution.y;

    bool mouse_was_pressed = texelFetch(iChannel2, ivec2(0), 0).r > 0.5f;
    float mouse_pressed_time = texelFetch(iChannel2, ivec2(0), 0).g;

    float debug_draw = mix(
        1.0f - clamp((pixel_pos.y - debug_pos) / 10.0f - 0.5f, 0.0f, 1.0f),
        clamp(mouse_pressed_time, 0.0f, 1.0f),
        mouse_was_pressed);


    int quad_index = 0;
    float width = 2.0f;
    if(pixel_pos.x < iResolution.x * 0.5f - width)
        quad_index = 1;
    if(pixel_pos.x > iResolution.x * 0.5f + width)
        quad_index = 2;

    if(quad_index == 1)
    {
        float time_pos = iTime;
        vec2 gyroid_pos = fragCoord.xy / (iResolution.x * 0.05f);
        float smooth_noise = sin(gyroid_pos.x) * cos(gyroid_pos.y) + sin(gyroid_pos.y) + cos(gyroid_pos.x);
        float time_offset = smooth_noise * grid_step2 * 0.5f;
        InterpNodes2 interp_nodes2 = GetLinearInterpNodes(time_pos + time_offset, grid_step2);
        for(int j = 0; j < 2; j++)
        {
            vec3 node2_hash = hash3i3f(ivec3(0, 0, interp_nodes2.indices[j]));
            float time_delta = time_pos - (interp_nodes2.positions[j]);

            vec2 uv = Rotate2(pixel_pos + pixel_velocity * time_delta, 2.0f * pi * node2_hash.x) / uv_tiling + node2_hash.yz;
            float weight = interp_nodes2.weights[j];
            linear_col += SrgbToLinear(texture(iChannel0, uv).rgb) * weight * mix(1.0f, (j > 0) ? 0.0f : 1.0f, debug_draw);
            moments += vec2(weight, weight * weight);
        }
    }
    if(quad_index == 2)
    {
        InterpNodes3 interp_nodes3 = GetHexGridInterpNodes(pixel_pos, vec2(grid_step3));
        for(int i = 0; i < 3; i++)
        {
            vec3 node3_hash = hash3i3f(interp_nodes3.indices[i].xyy);

            float time_pos = node3_hash.x * grid_step2 + iTime;
            InterpNodes2 interp_nodes2 = GetLinearInterpNodes(time_pos, grid_step2);

            vec2 pixel_delta = pixel_pos - interp_nodes3.positions[i];
            vec3 debug_col = vec3(0.0f);
            debug_col[i] = 1.0f;
            for(int j = 0; j < 2; j++)
            {
                vec3 node2_hash = hash3i3f(ivec3(interp_nodes3.indices[i].xy, interp_nodes2.indices[j]));
                float time_delta = time_pos - interp_nodes2.positions[j];
                vec2 uv = Rotate2(pixel_delta + pixel_velocity * time_delta, 2.0f * pi * node2_hash.x) / uv_tiling + node2_hash.yz;
                float weight = interp_nodes3.weights[i] * interp_nodes2.weights[j];
                linear_col += SrgbToLinear(texture(iChannel0, uv).rgb) * weight * mix(1.0f, (j > 0 || i > 0) ? 0.0f : 1.0f, debug_draw);
                moments += vec2(weight, weight * weight);
            }
        }
    }    

    vec3 mean_col = SrgbToLinear(textureLod(iChannel0, vec2(0.5f), 10.0f).rgb);
    vec3 linear_res = mix(PreserveVariance(linear_col, mean_col, moments.y), linear_col, debug_draw);
    fragColor = vec4(LinearToSrgb(linear_res), 1.0f);
    //InterpNodes2 interp_nodes2 = GetLinearInterpNodes(fragCoord.x, 200.0f);
    //fragColor = vec4(abs(interp_nodes2.positions[1] - fragCoord.x) / 20.0f);
    //fragColor = vec4(interp_nodes2.weights[1]);
}

