//Inspired by https://www.shadertoy.com/view/dtySDy

//http://www.jcgt.org/published/0009/03/02/paper.pdf
uvec3 hash33UintPcg(uvec3 v)
{
    v = v * 1664525u + 1013904223u;
    v.x += v.y*v.z; v.y += v.z*v.x; v.z += v.x*v.y;
    //v += v.yzx * v.zxy; //swizzled notation is not exactly the same because components depend on each other, but works too

    v ^= v >> 16u;
    v.x += v.y*v.z; v.y += v.z*v.x; v.z += v.x*v.y;
    //v += v.yzx * v.zxy;
    return v;
}

vec3 hash3i3f(ivec3 seed)
{
    uvec3 hash_uvec3 = hash33UintPcg(uvec3(seed));
    return vec3(hash_uvec3) * (1.0f / float(~0u));
}

const float pi = 3.141592f;

//Amazing resource explaining cube coordinates of hexagon grids:
//https://www.redblobgames.com/grids/hexagons/more-pixel-to-hex.html#justin-pombrio
mat2 skewed_to_grid = mat2(1.0f, 0.0f, 0.5f, sqrt(3.0f) / 2.0f);

vec2 GridToSkewed(vec2 grid_pos)
{
    return inverse(skewed_to_grid) * grid_pos;
}

vec2 SkewedToGrid(vec2 skewed_pos)
{
    return skewed_to_grid * skewed_pos;
}

vec3 SkewedToCube(vec2 skewed_pos)
{
    return vec3(skewed_pos.xy, -skewed_pos.x - skewed_pos.y);
}

//https://justinpombrio.net/programming/2020/04/28/pixel-to-hex.html
vec3 CubeToTri(vec3 cube_pos)
{
    return floor(cube_pos.xyz - cube_pos.zxy);
}

vec3 TriToHex(vec3 tri_index)
{
    return round((tri_index.xyz - tri_index.yzx) / 3.0f);
}

float max3(vec3 v)
{
    return max(max(v.x, v.y), v.z);
}
float HexOuterDist(vec3 cube_delta)
{
    return 1.0f - max3(abs(cube_delta.xyz - cube_delta.yzx));
}

float HexInnerDist(vec3 cube_delta)
{
    return length(cube_delta) / (sqrt(2.0f) / sqrt(3.0f)); //(sqrt(2.0f) / 2.0f) for inscribed normalization
}

struct InterpNodes3
{
    vec2 positions[3];
    ivec2 indices[3];
    vec3 weights;
};

InterpNodes3 GetHexGridInterpNodes(vec2 pos, vec2 grid_step)
{
    vec2 hex_size = grid_step * sqrt(3.0f);
    InterpNodes3 interp_nodes3;
    for(int i = 0; i < 3; i++)
    {
        vec2 grid_pos = pos / hex_size;
        vec2 skewed_offset = vec2(float(i) / 3.0f);
        vec2 skewed_pos = GridToSkewed(grid_pos) + skewed_offset;
        vec3 cube_pos = SkewedToCube(skewed_pos);
        vec3 hex_index = TriToHex(CubeToTri(cube_pos));
        vec2 hex_skewed_pos = hex_index.xy - skewed_offset;
        vec2 hex_grid_pos = SkewedToGrid(hex_skewed_pos);
        interp_nodes3.positions[i] = hex_grid_pos * hex_size;
        interp_nodes3.indices[i] = ivec2(hex_index.xy) * ivec2(3, 1) + ivec2(i, 0);
        interp_nodes3.weights[i] = HexOuterDist(cube_pos - hex_index);
    }
    return interp_nodes3;
}

mat2 Rotate2Mat(float ang)
{
    return mat2(cos(ang), sin(ang), -sin(ang), cos(ang));
}

vec2 Rotate2(vec2 vec, float ang)
{
    return Rotate2Mat(ang) * vec;
}

struct InterpNodes2
{
    vec2 positions;
    ivec2 indices;
    vec2 weights;
};
InterpNodes2 GetLinearInterpNodes(float position, float grid_step)
{
    float grid_pos = position / grid_step;
    vec2 global_phases = vec2(grid_pos * 0.5f) + vec2(0.5f, 0.0);
    vec2 indices = floor(global_phases) * 2.0f + vec2(0.0f, 1.0f);
    vec2 weights = vec2(1.0f) - abs(grid_pos - indices);
    return InterpNodes2(indices * grid_step, ivec2(indices), weights);
}

vec3 SrgbToLinear(vec3 srgb_col)
{
    return pow(srgb_col.rgb, vec3(2.2f));
}

vec3 LinearToSrgb(vec3 linear_col)
{
    return pow(linear_col.rgb, vec3(1.0f / 2.2f));
}

//Qizhi Yu, Fabrice Neyret, Eric Bruneton, and Nicolas Holzschuch. 2011. 
//Lagrangian Texture Advection: Preserving Both Spectrum and Velocity Field.
//IEEE Transactions on Visualization and Computer Graphics 17, 11 (2011), 1612â1623
vec3 PreserveVariance(vec3 linear_col, vec3 mean_col, float moment2)
{
    return (linear_col - mean_col) / sqrt(moment2) + mean_col;
}

