vec4 bokeh(sampler2D sampler, ivec2 uv, int r){
    vec4 col = vec4(0);
    float maxbright = 0.0;
    for (int x = -r; x <= r; x++){
        for (int y = -r; y <= r; y++){
            if (x*x + y*y < r*r){
                vec4 newcol = texture(sampler,(vec2(uv)+vec2(x,y))/iResolution.xy);
                float w = dot(newcol.rgb,newcol.rgb);
                if (w > maxbright){
                    col = newcol;
                    maxbright = w;
                }
            }
        }
    }
    return col;
}

