//based on
//https://shop-us.kurzgesagt.org/collections/new-stuff/products/cosmic-web-poster?variant=40612030119984

//blackbody by fishy: https://www.shadertoy.com/view/DtX3W8
#define responseCurve(w, p, v) sin(6.28318530718*clamp((v-p)/w + 0.5,0.0,1.0) - 1.57079632679)*0.5+0.5
vec3 getBlackbodyColor(float t)
{
    vec3 col = vec3(0), m = vec3(0);
    for(float v = 400.; v < 700.; v += 75.)
    {
        vec3 r = vec3(responseCurve(100., 440., v),
                      responseCurve(200., 550., v),
                      responseCurve(200., 600., v));
        m += r;
        col += 2.*v*v*v*(1./(exp(v/t) - 1.))*r;
    }
    return col/m;
}

//random functions from Dave Hoskins: //https://www.shadertoy.com/view/4djSRW
//  2 out, 2 in...
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

//  3 out, 3 in...
vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

vec2 voronoicell(vec2 uv){
    float n = 999.0;
    vec2 cell = vec2(0);
    for (int x = -1; x < 2; x++){
        for (int y = -1; y < 2; y++){
            vec2 cellpos = floor(uv) + vec2(x,y) + hash22(floor(uv) + vec2(x,y));
            float d = distance(cellpos,uv);
            if (d < n){
                n = d;
                cell = cellpos;
            }
        }
    }
    return cell;
}

float voronoi3dedges(vec3 uv){
    float d1 = 999.0;
    float d2 = 999.0;
    for (int x = -1; x < 2; x++){
        for (int y = -1; y < 2; y++){
            for (int z = -1; z < 2; z++){
                float d = distance(floor(uv) + vec3(x,y,z) + hash33(floor(uv) + vec3(x,y,z)),uv);
                if (d < d1) {
                    d2 = d1;
                    d1 = d;
                } else if (d < d2){
                    d2 = d;
                }
            }
        }
    }
    return (abs(d1-d2) + 1.0 - d1)/2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*128.0;
    vec2 cell = voronoicell(uv)/16.0;

    float rand = 0.1*voronoi3dedges(vec3(cell*5.0,10));
    float cracks = voronoi3dedges(vec3(cell,0));
    float lines = voronoi3dedges(vec3(cell,10));
    float temp = 30.0 - cracks*30.0 - rand*40.0;

    // Time varying pixel color
    vec3 base = mix(vec3(0.3,0.2,0.4),vec3(0.1,0.1,0.2),smoothstep(0.0,0.3,lines + rand));
    vec3 col = mix(base,getBlackbodyColor(temp)+vec3(0.1,0.1,0.2),smoothstep(0.0,1.0,(temp-20.0)*0.25));

    // Output to screen
    fragColor = vec4(col,1.0);
}

