vec3 hsvToRgb(vec3 hsv){ //hsv.x = hue, hsv.y = saturation, hsv.z = value
    vec3 col = vec3(hsv.x, hsv.x + 2.0/3.0, hsv.x + 4.0/3.0); //inputs for r, g, and b
    col = clamp(abs(mod(col*2.0, 2.0)-1.0)*3.0 - 1.0, 0.0, 1.0)*hsv.z*hsv.y + hsv.z - hsv.z*hsv.y; //hue function (graph it on desmos)
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    //make sure your mouse coordinates in buffer A are in uv coords
    vec2 uv = (fragCoord - iResolution.xy/2.0)/iResolution.y;
    float pxunit = 1.0/iResolution.y; //the size of a pixel for blending

    //angle of slider
    float a = texelFetch(iChannel0, ivec2(0,0), 0).x;
    //position of slider handle
    vec2 pos = vec2(cos(a),sin(a))*sliderRadius;

    vec3 col = mix(vec3(0.8),vec3(1),float(abs(mod(uv.x,0.1) - mod(uv.y,0.1)) > 0.05));
    float innerdist = sliderWidth - abs(length(uv) - sliderRadius);
    float handledist = sliderWidth - distance(uv,pos);
    col = mix(col, hsvToRgb(vec3(atan(uv.y,uv.x)/2.0/PI,1.0,1.0)), smoothstep(0.0, pxunit, innerdist)); //slider background
    col = mix(col, vec3(0.2), smoothstep(0.0, pxunit, 0.002-abs(innerdist))); //slider border
