void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 data = vec4(0);
    //x: angle of slider (radians)
    //y: initial offset (radians)
    //z: handle is held (bool)
    if (floor(fragCoord) == vec2(0,0)){
        vec4 prevdata = texelFetch(iChannel0, ivec2(0,0), 0); //previous slider pos
        if (iMouse.w > 0.0){ //setup on click
            vec2 m = (iMouse.zw - iResolution.xy/2.0)/iResolution.y; //last click pos in uv coords
            if (distance(m,vec2(cos(prevdata.x), sin(prevdata.x))*sliderRadius) <= sliderWidth){
                vec2 d = normalize(m);
                data.y = atan(d.y,d.x) - prevdata.x;
                data.x = prevdata.x;
                data.z = 1.0;
            }
        }
        if (iMouse.z > 0.0 && (prevdata.z == 1.0 || data.z == 1.0)){ //update while held
            data.z = 1.0;
            data.y = iMouse.w > 0.0 ? data.y : prevdata.y;
            vec2 m = (iMouse.xy - iResolution.xy/2.0)/iResolution.y;
            float r1 = length(m);
            vec2 d = m/r1; //direction from (0,0) to mouse in uv coords
            if (r1 < sliderRadius-sliderWidth && doDeadZone) { //creates center dead zone
                data.x = prevdata.x;
            } else {
                vec2 h = vec2(cos(prevdata.x + data.y),sin(prevdata.x + data.y))*r1;
                vec2 p = vec2(cos(prevdata.x),sin(prevdata.x))*sliderRadius;
                data.x = atan(d.y,d.x);
                if (distance(h,p) > sliderWidth){ //keeps the handle under the cursor
                    if (sliderWidth - abs(r1 - sliderRadius) < 0.0){ //cursor isn't on the track
                        data.y = 0.0;
                    } else { //don't ask
                        float o = acos((r1*r1 + p.x*p.x + p.y*p.y - sliderWidth*sliderWidth)/(2.0*r1*sliderRadius));
                        data.y = sign(dot(vec2(-p.y,p.x),h))*o;
                    }
                }
                data.x -= data.y;
            }
        } else { //store when not clicked
            data.z = 0.0;
            data.x = prevdata.x;
        }
        if (iFrame == 0) data.x = sliderInitialValue; //set initial value
    }
    fragColor = data;
}

